    public class SistemaDirecciones : IDirectionSystem
    {
        private readonly Dictionary<string, string> _opuestos;
        private readonly HashSet<string> _direccionesValidas;
        private readonly Dictionary<int, string> _clavesDirecciones;

        public SistemaDirecciones()
        {
            _opuestos = new Dictionary<string, string>(StringComparer.OrdinalIgnoreCase)
            {
                ["norte"] = "sur",
                ["noreste"] = "suroeste",
                ["este"] = "oeste",
                ["sureste"] = "noroeste",
                ["sur"] = "norte",
                ["suroeste"] = "noreste",
                ["oeste"] = "este",
                ["noroeste"] = "sureste",
                ["arriba"] = "abajo",
                ["abajo"] = "arriba",
                ["adentro"] = "afuera",
                ["afuera"] = "adentro"
            };

            _direccionesValidas = new HashSet<string>(_opuestos.Keys, StringComparer.OrdinalIgnoreCase);

            _clavesDirecciones = new Dictionary<int, string>
            {
                [0] = DirectionLanguageKeys.Direccion_Norte,
                [1] = DirectionLanguageKeys.Direccion_Noreste,
                [2] = DirectionLanguageKeys.Direccion_Este,
                [3] = DirectionLanguageKeys.Direccion_Sureste,
                [4] = DirectionLanguageKeys.Direccion_Sur,
                [5] = DirectionLanguageKeys.Direccion_Suroeste,
                [6] = DirectionLanguageKeys.Direccion_Oeste,
                [7] = DirectionLanguageKeys.Direccion_Noroeste,
                [8] = DirectionLanguageKeys.Direccion_Arriba,
                [9] = DirectionLanguageKeys.Direccion_Abajo,
                [10] = DirectionLanguageKeys.Direccion_Adentro,
                [11] = DirectionLanguageKeys.Direccion_Afuera
            };
        }

