        {
            if (!_clavesDirecciones.TryGetValue(directionId, out var key))
                throw new ArgumentException($"ID de direccin invlido: {directionId}");

            return LanguageManager.GetText(key).ToLowerInvariant();
        }

        public string GetOppositeDirection(string direction)
        {
            if (!_opuestos.TryGetValue(direction.ToLower(), out var opposite))
                throw new ArgumentException($"Direccin desconocida: {direction}");

            return opposite;
        }

        public IEnumerable<string> GetAllDirections()
        {
            return _direccionesValidas;
        }

        public bool IsValidDirection(string direction)
        {
            return _direccionesValidas.Contains(direction);
        }

        public string GetDirectionDisplayName(string direction)
        {
            if (!IsValidDirection(direction))
                throw new ArgumentException($"Direccin invlida: {direction}");

            var key = _clavesDirecciones.FirstOrDefault(x => 
                GetDirectionName(x.Key).Equals(direction, StringComparison.OrdinalIgnoreCase)).Value;

            if (key == null)
                throw new InvalidOperationException($"No se encontr clave de idioma para la direccin: {direction}");

            return LanguageManager.GetText(key);
        }
    }
}
