<antArtifact identifier="refined-world-model" type="application/vnd.ant.code" language="csharp" title="Updated WorldModel with Direction System Support">
namespace IFWorldModel
{
    public class WorldModel : IGraphOperations
    {
        private readonly IGraphOperations _graph;
        private readonly IDirectionSystem _directionSystem;

        public WorldModel(IDirectionSystem directionSystem)
        {
            _graph = new Graph();
            _directionSystem = directionSystem ?? throw new ArgumentNullException(nameof(directionSystem));
        }

        // IGraphOperations implementation remains the same
        #region IGraphOperations Implementation
        public IReadOnlyDictionary<Guid, INode> Nodes => _graph.Nodes;
        public void AddNode(INode node) => _graph.AddNode(node);
        public void RemoveNode(Guid nodeId) => _graph.RemoveNode(nodeId);
        public IEdge CreateEdge(Guid sourceId, Guid targetId) => _graph.CreateEdge(sourceId, targetId);
        public void RemoveEdge(IEdge edge) => _graph.RemoveEdge(edge);
        public void ConnectNodes(INode source, INode target) => _graph.ConnectNodes(source, target);
        public void SetEdgeProperty(IEdge edge, string propertyName, object value) => _graph.SetEdgeProperty(edge, propertyName, value);
        public void AddObserver(IGraphEventObserver observer) => _graph.AddObserver(observer);
        public void RemoveObserver(IGraphEventObserver observer) => _graph.RemoveObserver(observer);
        #endregion

        #region Builder Factory Methods
        public RoomBuilder CreateRoom(string name, string description)
        {
            var room = new Room(name, description);
            AddNode(room);
            return new RoomBuilder(this, room, _directionSystem);
        }

        // Other builder methods remain the same but pass _directionSystem to any that need it
        #endregion

        #region Direction-Aware Methods
        public string GetExitDisplayName(IEdge exit)
        {
            var direction = exit.GetSourcePropertyValue<string>("Direction");
            return direction != null ? _directionSystem.GetDirectionDisplayName(direction) : string.Empty;
        }

        public IEnumerable<(string Direction, Room Destination)> GetRoomExits(Room room)
        {
            if (room == null)
                throw new ArgumentNullException(nameof(room));

            return room.Edges
                .Where(e => e.Target is Room || (e.Target is Door door && door.IsOpen))
                .Select(e => (
                    Direction: GetExitDisplayName(e),
                    Destination: GetExitDestination(room, e)
                ))
                .Where(exit => exit.Destination != null);
        }

        public string GetRoomDescription(Room room, Guid playerId)
        {
            var description = room.GetFullDescription();
            var contents = GetRoomContents(room).Where(t => t.Id != playerId && t.IsVisible());
            var exits = GetRoomExits(room);

            if (contents.Any())
                description += $"\n\n{LanguageManager.GetText(LanguageKeys.Look_YouSee)}: {string.Join(", ", contents.Select(t => t.Name))}";

            if (exits.Any())
                description += $"\n\n{LanguageManager.GetText(LanguageKeys.Look_Exits)}: {string.Join(", ", exits.Select(e => e.Direction))}";

            return description;
        }
        #endregion

        // Rest of WorldModel implementation remains the same
    }
}
</antArtifact>

