                .Where(e => e.Target is Room || (e.Target is Door door && door.IsOpen))
                .Select(e => (
                    Direction: GetExitDisplayName(e),
                    Destination: GetExitDestination(room, e)
                ))
                .Where(exit => exit.Destination != null);
        }

