            if (exits.Any())
                description += $"\n\n{LanguageManager.GetText(LanguageKeys.Look_Exits)}: {string.Join(", ", exits.Select(e => e.Direction))}";

            return description;
        }

        public string GetExitDisplayName(IEdge exit)
        {
            var direction = exit.GetSourcePropertyValue<string>("Direction");
            return direction != null ? _directionSystem.GetDirectionDisplayName(direction) : string.Empty;
        }
        #endregion

        #region World Manipulation
        public WorldModel PlaceItem(Thing item, Thing location)
        {
            MoveItem(item, null, location);
            return this;
        }

        public bool MoveItem(Thing item, Thing fromLocation, Thing toLocation)
        {
            try
            {
                if (fromLocation != null)
                {
                    var fromEdge = item.Edges.FirstOrDefault(e => e.Target == fromLocation);
                    if (fromEdge != null)
                    {
                        RemoveEdge(fromEdge);
                    }
                }

                if (toLocation != null)
                {
                    var edge = new Edge();
                    edge.ConnectNodes(item, toLocation);
                }

                return true;
            }
            catch (Exception)
            {
                return false;
            }
        }

        public bool MovePlayerToRoom(Guid playerId, Room destination)
        {
            if (GetThingById(playerId) is not Person player || destination == null)
                return false;

            var currentRoom = GetPlayerLocation(playerId);
            return MoveItem(player, currentRoom, destination);
        }

        public bool MoveThingFromRoomToPlayer(Guid playerId, Thing item, Room room)
        {
            if (GetThingById(playerId) is not Person player)
                return false;

            if (MoveItem(item, room, player))
            {
                player.AddToInventory(item);
                return true;
            }
            return false;
        }

        public bool MoveThingFromPlayerToRoom(Guid playerId, Thing item, Room room)
        {
            if (GetThingById(playerId) is not Person player)
                return false;

            if (MoveItem(item, player, room))
            {
                player.RemoveFromInventory(item);
                return true;
            }
            return false;
        }
        #endregion

        #region Helper Methods
        private Room GetExitDestination(Room sourceRoom, IEdge exit) =>
            exit.Target is Room room ? room :
            exit.Target is Door door && door.IsOpen ? GetRoomBeyondDoor(sourceRoom, door) :
            null;

        private Room GetRoomBeyondDoor(Room currentRoom, Door door) =>
            door.Edges
                .Where(e => e.Target is Room && e.Target != currentRoom)
                .Select(e => e.Target as Room)
                .FirstOrDefault();
        #endregion
    }
}
