 Let's create the English-specific grammar implementation that extends our base grammar interface:

```typescript
/**
 * languages/en-US/grammar.ts
 * English language implementation of the grammar interface
 */

import {
  Token,
  TokenType,
  TaggedWord,
  PartOfSpeech,
  Phrase,
  PhraseType,
  CommandPattern,
  ParsedCommand,
  ParsingError,
  BaseGrammar
} from '../../core/grammar';

// Common English articles
const ARTICLES = ['a', 'an', 'the'];

// Common English prepositions
const PREPOSITIONS = [
  'about', 'above', 'across', 'after', 'against', 'along', 'among', 'around',
  'at', 'before', 'behind', 'below', 'beneath', 'beside', 'between', 'beyond',
  'by', 'down', 'during', 'except', 'for', 'from', 'in', 'inside', 'into',
  'like', 'near', 'of', 'off', 'on', 'onto', 'out', 'outside', 'over',
  'through', 'to', 'toward', 'under', 'underneath', 'until', 'up', 'upon',
  'with', 'within', 'without'
];

// Common English pronouns
const PRONOUNS = [
  'i', 'me', 'my', 'mine',
  'you', 'your', 'yours',
  'he', 'him', 'his',
  'she', 'her', 'hers',
  'it', 'its',
  'we', 'us', 'our', 'ours',
  'they', 'them', 'their', 'theirs',
  'this', 'that', 'these', 'those'
];

// Common English conjunctions
const CONJUNCTIONS = ['and', 'or', 'but', 'nor', 'so', 'yet', 'for'];

/**
 * English language grammar implementation
 */
export class EnglishGrammar extends BaseGrammar {
  // Verb dictionary for English
  private verbs: Set<string>;

  // Noun dictionary for English
  private nouns: Set<string>;

  // Adjective dictionary for English
  private adjectives: Set<string>;

  // Adverb dictionary for English
  private adverbs: Set<string>;

  // Command patterns specific to English
  private commandPatterns: CommandPattern[];

  /**
   * Initialize the English grammar with dictionaries and patterns
   */
  constructor() {
    super();

    // Initialize with basic dictionaries
    // In a real implementation, these would be much more comprehensive
    this.verbs = new Set([
      'look', 'examine', 'take', 'get', 'drop', 'put', 'give', 'show',
      'go', 'move', 'walk', 'run', 'climb', 'jump', 'open', 'close',
      'turn', 'push', 'pull', 'use', 'talk', 'speak', 'ask', 'tell',
      'attack', 'kill', 'hit', 'throw', 'eat', 'drink', 'wear', 'remove',
      'read', 'search', 'enter', 'exit', 'leave', 'inventory', 'help',
      'direct', 'wave'  // For "Reflections" mirror mechanics
    ]);

    this.nouns = new Set([
      'door', 'key', 'room', 'table', 'chair', 'book', 'box', 'light',
      'window', 'knife', 'sword', 'gun', 'food', 'water', 'bottle', 'bag',
      'coin', 'money', 'paper', 'note', 'letter', 'pen', 'pencil', 'map',
      'mirror', 'painting', 'wall', 'floor', 'ceiling', 'stairs', 'hallway',
      'north', 'south', 'east', 'west', 'up', 'down', 'in', 'out'
    ]);

    this.adjectives = new Set([
      'red', 'blue', 'green', 'yellow', 'black', 'white', 'brown', 'gray',
      'large', 'small', 'big', 'tiny', 'huge', 'long', 'short', 'tall',
      'heavy', 'light', 'sharp', 'dull', 'old', 'new', 'broken', 'fixed',
      'open', 'closed', 'locked', 'unlocked', 'dark', 'bright', 'shiny',
      'reflective'  // Especially for "Reflections"
    ]);

    this.adverbs = new Set([
      'quickly', 'slowly', 'carefully', 'quietly', 'loudly', 'silently',
      'gently', 'roughly', 'lightly', 'heavily', 'again'
    ]);

    // Define common command patterns in English
    this.commandPatterns = [
      // VERB (look, inventory)
      {
        id: 'verb_only',
        description: 'Single verb command',
        structure: [PhraseType.VERB_PHRASE],
        argumentMapping: { 'verb': 0 }
      },

      // VERB + NOUN (take key, examine mirror)
      {
        id: 'verb_direct_object',
        description: 'Verb with direct object',
        structure: [PhraseType.VERB_PHRASE, PhraseType.NOUN_PHRASE],
        argumentMapping: { 'verb': 0, 'direct_object': 1 }
      },

      // VERB + NOUN + PREPOSITION + NOUN (put key in lock, throw ball at window)
      {
        id: 'verb_direct_object_prep_indirect',
        description: 'Verb with direct object and prepositional phrase',
        structure: [
          PhraseType.VERB_PHRASE,
          PhraseType.NOUN_PHRASE,
          PhraseType.PREPOSITIONAL_PHRASE
        ],
        argumentMapping: {
          'verb': 0,
          'direct_object': 1,
          'preposition': 2
        }
      },

      // VERB + NOUN + TO + NOUN (give key to guard, direct mirror to painting)
      {
        id: 'verb_direct_to_indirect',
        description: 'Verb with direct and indirect objects',
        structure: [
          PhraseType.VERB_PHRASE,
          PhraseType.NOUN_PHRASE,
          PhraseType.PREPOSITIONAL_PHRASE
        ],
        argumentMapping: {
          'verb': 0,
          'direct_object': 1,
          'indirect_object': 2
        }
      }
    ];
  }

  /**
   * Tokenize English text into individual tokens
   */
  tokenize(input: string): Token[] {
    // Simple tokenization by splitting on whitespace and handling punctuation
    const tokens: Token[] = [];

    // Normalize input by trimming and converting to lowercase
    const normalizedInput = input.trim().toLowerCase();

    // Simple regex to match words, numbers, and punctuation
    const tokenRegex = /[a-z0-9]+|[.,;:!?'"()\[\]{}]/gi;
    let match;

    while ((match = tokenRegex.exec(normalizedInput)) !== null) {
      const text = match[0];
      const position = match.index;

      // Determine token type
      let type: TokenType;
      if (/^\d+$/.test(text)) {
        type = TokenType.NUMBER;
      } else if (/[.,;:!?'"()\[\]{}]/.test(text)) {
        type = TokenType.PUNCTUATION;
      } else {
        type = TokenType.WORD;
      }

      tokens.push({
        text,
        normalized: text.toLowerCase(),
        type,
        position
      });
    }

    return tokens;
  }

  /**
   * Tag tokens with their parts of speech
   */
