    const verbsWithPrepositions = [
      'put', 'give', 'direct'
    ];

    if (
      verbsWithPrepositions.includes(command.verb.toLowerCase()) &&
      Object.keys(command.prepositions).length === 0
    ) {
      if (command.verb.toLowerCase() === 'put') {
