// packages/core/src/execution/handlers/movement-handler.ts

import { ParsedCommand } from '../../parser/core/types';
import { Entity, EntityId, RelationshipType } from '../../world-model/types';
import { CommandResult, GameContext } from '../types';
import { BaseCommandHandler } from '../command-handler';
import { createEvent } from '../../events/event-system';
import { StandardEventTypes, StandardEventTags } from '../../events/standard-events';

/**
 * Handler for movement commands
 */
export class MovementHandler extends BaseCommandHandler {
  // Standard cardinal directions and their opposites
  private static readonly DIRECTIONS = {
    'north': 'south',
    'south': 'north',
    'east': 'west',
    'west': 'east',
    'northeast': 'southwest',
    'southwest': 'northeast',
    'northwest': 'southeast',
    'southeast': 'northwest',
    'up': 'down',
    'down': 'up',
    'in': 'out',
    'out': 'in'
  };

  // Abbreviations for directions
  private static readonly DIRECTION_ALIASES: Record<string, string> = {
    'n': 'north',
    's': 'south',
    'e': 'east',
    'w': 'west',
    'ne': 'northeast',
    'nw': 'northwest',
    'se': 'southeast',
    'sw': 'southwest',
    'u': 'up',
    'd': 'down'
  };

  constructor() {
    // Register all movement verbs
    super([
      'go', 'move', 'walk', 'run', 'travel', 'head', 
      'climb', 'enter', 'exit', 'leave',
      // Include all directions as standalone commands
      ...Object.keys(MovementHandler.DIRECTIONS),
      ...Object.keys(MovementHandler.DIRECTION_ALIASES)
    ]);
  }

  /**
   * Execute the movement command
   */
  public execute(command: ParsedCommand, context: GameContext): CommandResult {
    const { player, currentLocation } = context;

    // Determine the direction to move
    let direction = this.getDirectionFromCommand(command);

    if (!direction) {
      return this.createFailureResult('Which direction do you want to go?');
    }

    // Normalize any direction aliases
    direction = MovementHandler.DIRECTION_ALIASES[direction] || direction;

    // Handle the special case of entering/exiting a specific object
    if (command.verb === 'enter' && command.directObject) {
      return this.handleEnterObject(command.directObject, context);
    }

    if (command.verb === 'exit' || command.verb === 'leave') {
      return this.handleExit(context);
    }

    // Find the exit in the given direction
    const exit = this.findExit(direction, currentLocation, context);

    if (!exit) {
      return this.createFailureResult(`You can't go ${direction} from here.`);
    }

    // Check if the exit is accessible
    if (exit.attributes.accessible === false) {
      const blockageReason = exit.attributes.blockageReason as string || 'The way is blocked.';
      return this.createFailureResult(blockageReason);
    }

    // Find the destination
    const destinationId = this.getDestination(exit, currentLocation.id);
    if (!destinationId) {
      return this.createFailureResult(`Something went wrong with the ${direction} exit.`);
    }

    // Move to the destination
    return this.moveToLocation(destinationId, direction, context);
  }

  /**
   * Validate a movement command
   */
  public validate(command: ParsedCommand, context: GameContext): { valid: boolean; error?: string } {
    // Basic validation - check if we have a direction
    const direction = this.getDirectionFromCommand(command);

    if (!direction) {
      // Special case for enter/exit
      if (command.verb === 'enter' && command.directObject) {
        const targetEntity = context.findEntityByName(command.directObject);
        if (!targetEntity) {
          return {
            valid: false,
            error: `You don't see any ${command.directObject} here to enter.`
          };
        }

        if (targetEntity.attributes.enterable !== true) {
          return {
            valid: false,
            error: `You can't enter the ${command.directObject}.`
          };
        }

        return { valid: true };
      }

      if (command.verb === 'exit' || command.verb === 'leave') {
        // Check if we're inside something that can be exited
        const insideIds = context.player.relationships[RelationshipType.CONTAINED_BY] || [];
        if (insideIds.length === 0 || insideIds[0] === context.currentLocation.id) {
          return {
            valid: false,
            error: 'You\'re not inside anything that you can exit.'
          };
        }

        return { valid: true };
      }

      return {
        valid: false,
        error: 'Which direction do you want to go?'
      };
    }

    // Normalize any direction aliases
    const normalizedDirection = MovementHandler.DIRECTION_ALIASES[direction] || direction;

    // Check if there's an exit in that direction
    const exit = this.findExit(normalizedDirection, context.currentLocation, context);
    if (!exit) {
      return {
        valid: false,
        error: `You can't go ${normalizedDirection} from here.`
      };
    }

    // Check if the exit is accessible
    if (exit.attributes.accessible === false) {
      const blockageReason = exit.attributes.blockageReason as string || 'The way is blocked.';
      return {
        valid: false,
        error: blockageReason
      };
    }

    return { valid: true };
  }

  /**
   * Find an exit in the given direction
   */
  private findExit(direction: string, location: Entity, context: GameContext): Entity | undefined {
    // Look for a connection with the matching direction
    const connections = location.relationships[RelationshipType.CONNECTS_TO] || [];

    for (const connectionId of connections) {
      const connection = context.getEntity(connectionId);
      if (!connection) continue;

      // Check if this is the exit we're looking for
      if (
        connection.attributes.direction === direction ||
        connection.attributes.aliases?.includes(direction)
      ) {
        return connection;
      }
    }

    return undefined;
  }

  /**
   * Get the destination location ID from an exit
   */
  private getDestination(exit: Entity, currentLocationId: EntityId): EntityId | undefined {
    // The destination is the location connected to this exit that isn't our current location
    const connections = exit.relationships[RelationshipType.CONNECTS_TO] || [];
    return connections.find(id => id !== currentLocationId);
  }

  /**
   * Handle entering a specific object (like a vehicle or a building)
   */
  private handleEnterObject(objectName: string, context: GameContext): CommandResult {
    const { player, currentLocation } = context;

    // Find the entity being entered
    const targetEntity = context.findEntityByName(objectName);

    if (!targetEntity) {
      return this.createFailureResult(`You don't see any ${objectName} here to enter.`);
    }

    // Check if the entity is visible and accessible
    if (!context.isVisible(targetEntity.id) || !context.isAccessible(targetEntity.id)) {
      return this.createFailureResult(`You don't see any ${objectName} here to enter.`);
    }

    // Check if the entity can be entered
    if (targetEntity.attributes.enterable !== true) {
      return this.createFailureResult(`You can't enter the ${objectName}.`);
    }

    // Move the player inside the object
    // This involves:
    // 1. Updating the player's CONTAINED_BY relationship
    // 2. Updating the object's CONTAINS relationship
    // 3. Possibly updating the current location (if entering a location-like object)

    // Create an updated world state
    const updatedContext = context.updateWorldState(state => {
      const updatedState = { ...state };

      // Get the current entities
      const playerEntity = { ...player };
      const targetObjectEntity = { ...targetEntity };

      // Update player's containment relationship
      playerEntity.relationships = {
        ...playerEntity.relationships,
        [RelationshipType.CONTAINED_BY]: [targetEntity.id]
      };

      // Update target object's containment relationship
      const currentContains = targetObjectEntity.relationships[RelationshipType.CONTAINS] || [];
      targetObjectEntity.relationships = {
        ...targetObjectEntity.relationships,
        [RelationshipType.CONTAINS]: [...currentContains, player.id]
      };

      // Update the entities in state
      updatedState.entities = {
        ...updatedState.entities,
        [player.id]: playerEntity,
        [targetEntity.id]: targetObjectEntity
      };

      return updatedState;
    });

    // Create the movement event
    const moveEvent = createEvent(
      StandardEventTypes.PLAYER_MOVED,
      {
        fromId: currentLocation.id,
        fromName: currentLocation.attributes.name || 'previous location',
        toId: targetEntity.id,
        toName: targetEntity.attributes.name || objectName,
        direction: 'in',
        enteredObject: true,
        arrivalDescription: targetEntity.attributes.insideDescription || `You are now inside the ${objectName}.`
      },
      {
        actor: player.id,
        location: targetEntity.id,
        tags: [StandardEventTags.VISIBLE]
      }
    );

    // Create discovery event if this is the first time entering
    const events = [moveEvent];
    if (targetEntity.attributes.discovered !== true) {
      const discoveryEvent = createEvent(
        StandardEventTypes.LOCATION_DISCOVERED,
        {
          locationId: targetEntity.id,
          locationName: targetEntity.attributes.name || objectName
        },
        {
          actor: player.id,
          location: targetEntity.id,
          tags: [StandardEventTags.DISCOVERY]
        }
      );
      events.push(discoveryEvent);

      // Mark the location as discovered in the world state
      context.updateWorldState(state => {
        const updatedState = { ...state };
        const updatedTarget = { 
          ...updatedState.entities[targetEntity.id],
          attributes: {
            ...updatedState.entities[targetEntity.id].attributes,
            discovered: true
          }
        };

        updatedState.entities = {
          ...updatedState.entities,
          [targetEntity.id]: updatedTarget
        };

        return updatedState;
      });
    }

    return this.createSuccessResult(events);
  }

  /**
   * Handle exiting from a container or vehicle
   */
  private handleExit(context: GameContext): CommandResult {
    const { player, currentLocation } = context;

    // Check if the player is inside something that can be exited
    const containerIds = player.relationships[RelationshipType.CONTAINED_BY] || [];
    if (containerIds.length === 0 || containerIds[0] === currentLocation.id) {
      return this.createFailureResult('You\'re not inside anything that you can exit.');
    }

    // The container is the first item in the CONTAINED_BY relationship
    const containerId = containerIds[0];
    const container = context.getEntity(containerId);

    if (!container) {
      return this.createFailureResult('Something went wrong trying to exit.');
    }

    // Find the location that contains this container
    const locationIds = container.relationships[RelationshipType.CONTAINED_BY] || [];
    if (locationIds.length === 0) {
      return this.createFailureResult('Something went wrong trying to exit.');
    }

    // The location is the first item in the container's CONTAINED_BY relationship
    const locationId = locationIds[0];
    const location = context.getEntity(locationId);

    if (!location) {
      return this.createFailureResult('Something went wrong trying to exit.');
    }

    // Move the player to the location
    const updatedContext = context.updateWorldState(state => {
      const updatedState = { ...state };

      // Get the current entities
      const playerEntity = { ...player };
      const containerEntity = { ...container };
      const locationEntity = { ...location };

      // Update player's containment relationship
      playerEntity.relationships = {
        ...playerEntity.relationships,
        [RelationshipType.CONTAINED_BY]: [locationId]
      };

      // Update container's containment relationship
      const containerContains = containerEntity.relationships[RelationshipType.CONTAINS] || [];
      containerEntity.relationships = {
        ...containerEntity.relationships,
        [RelationshipType.CONTAINS]: containerContains.filter(id => id !== player.id)
      };

      // Update location's containment relationship
      const locationContains = locationEntity.relationships[RelationshipType.CONTAINS] || [];
      locationEntity.relationships = {
        ...locationEntity.relationships,
        [RelationshipType.CONTAINS]: [...locationContains, player.id]
      };

      // Update the entities in state
      updatedState.entities = {
        ...updatedState.entities,
        [player.id]: playerEntity,
        [containerId]: containerEntity,
        [locationId]: locationEntity
      };

      return updatedState;
    });

    // Create the movement event
    const moveEvent = createEvent(
      StandardEventTypes.PLAYER_MOVED,
      {
        fromId: containerId,
        fromName: container.attributes.name || 'container',
        toId: locationId,
        toName: location.attributes.name || 'location',
        direction: 'out',
        exitedObject: true,
        arrivalDescription: location.attributes.description || `You exit the ${container.attributes.name || 'container'}.`
      },
      {
        actor: player.id,
        location: locationId,
        tags: [StandardEventTags.VISIBLE]
      }
    );

    return this.createSuccessResult([moveEvent]);
  }

  /**
   * Move the player to a new location
   */
  private moveToLocation(destinationId: EntityId, direction: string, context: GameContext): CommandResult {
    const { player, currentLocation } = context;

    // Get the destination entity
    const destination = context.getEntity(destinationId);
    if (!destination) {
      return this.createFailureResult('Something went wrong with the movement.');
    }

    // Move the player from current location to destination
    // This involves:
    // 1. Removing player from current location's CONTAINS
    // 2. Adding player to destination's CONTAINS
    // 3. Updating player's CONTAINED_BY to destination

    // Update world state
    const updatedContext = context.updateWorldState(state => {
      const updatedState = { ...state };

      // Get the current entities
      const playerEntity = { ...player };
      const currentLocationEntity = { ...currentLocation };
      const destinationEntity = { ...destination };

      // Update player's containment relationship
      playerEntity.relationships = {
        ...playerEntity.relationships,
        [RelationshipType.CONTAINED_BY]: [destinationId]
      };

      // Update current location's containment relationship
      const currentContains = currentLocationEntity.relationships[RelationshipType.CONTAINS] || [];
      currentLocationEntity.relationships = {
        ...currentLocationEntity.relationships,
        [RelationshipType.CONTAINS]: currentContains.filter(id => id !== player.id)
      };

      // Update destination's containment relationship
      const destContains = destinationEntity.relationships[RelationshipType.CONTAINS] || [];
      destinationEntity.relationships = {
        ...destinationEntity.relationships,
        [RelationshipType.CONTAINS]: [...destContains, player.id]
      };

      // Update the entities in state
      updatedState.entities = {
        ...updatedState.entities,
        [player.id]: playerEntity,
        [currentLocation.id]: currentLocationEntity,
        [destinationId]: destinationEntity
      };

      // Update metadata to reflect new location
      updatedState.meta = {
        ...updatedState.meta,
        focusEntityId: destinationId
      };

      return updatedState;
    });

    // Create the movement event
    const moveEvent = createEvent(
      StandardEventTypes.PLAYER_MOVED,
      {
        fromId: currentLocation.id,
        fromName: currentLocation.attributes.name || 'previous location',
        toId: destinationId,
        toName: destination.attributes.name || 'new location',
        direction,
        arrivalDescription: destination.attributes.description || `You arrive at ${destination.attributes.name || 'a new location'}.`
      },
      {
        actor: player.id,
        location: destinationId,
        tags: [StandardEventTags.VISIBLE]
      }
    );

    // Create discovery event if this is the first time visiting
    const events = [moveEvent];
    if (destination.attributes.discovered !== true) {
      const discoveryEvent = createEvent(
        StandardEventTypes.LOCATION_DISCOVERED,
        {
          locationId: destinationId,
          locationName: destination.attributes.name || 'new location'
        },
        {
          actor: player.id,
          location: destinationId,
          tags: [StandardEventTags.DISCOVERY]
        }
      );
      events.push(discoveryEvent);

      // Mark the location as discovered in the world state
      context.updateWorldState(state => {
        const updatedState = { ...state };
        const updatedDestination = { 
          ...updatedState.entities[destinationId],
          attributes: {
            ...updatedState.entities[destinationId].attributes,
            discovered: true
          }
        };

        updatedState.entities = {
          ...updatedState.entities,
          [destinationId]: updatedDestination
        };

        return updatedState;
      });
    }

    return this.createSuccessResult(events);
  }

  /**
   * Extract the movement direction from the command
   */
  private getDirectionFromCommand(command: ParsedCommand): string | undefined {
    // Case 1: The verb itself is a direction (e.g., "north")
    if (Object.keys(MovementHandler.DIRECTIONS).includes(command.verb) || 
        Object.keys(MovementHandler.DIRECTION_ALIASES).includes(command.verb)) {
      return command.verb;
    }

    // Case 2: The direct object is a direction (e.g., "go north")
    if (command.directObject) {
      if (Object.keys(MovementHandler.DIRECTIONS).includes(command.directObject) ||
          Object.keys(MovementHandler.DIRECTION_ALIASES).includes(command.directObject)) {
        return command.directObject;
      }
    }

    // Case 3: Direction is in a preposition (e.g., "walk to north")
    for (const [prep, obj] of Object.entries(command.prepositions)) {
      if (Object.keys(MovementHandler.DIRECTIONS).includes(obj) ||
          Object.keys(MovementHandler.DIRECTION_ALIASES).includes(obj)) {
        return obj;
      }
    }

    return undefined;
  }
}

/**
 * Create a new movement command handler
 */
export function createMovementHandler(): MovementHandler {
  return new MovementHandler();
}

// packages/core/src/text-generation/text-generator.ts

import { SemanticEvent } from '../events/types';
import { ChannelMessage } from '../channels/types';
import { EventSource } from '../events/event-system';
import { ChannelManager } from '../channels/channel-system';
import { Entity, EntityId } from '../world-model/types';
import { GameContext } from '../execution/types';

/**
 * Template for generating text from an event or message
 */
export interface TextTemplate {
  /**
   * Unique identifier for this template
   */
  id: string;

  /**
   * Event type or channel message type this template handles
   */
  type: string;

  /**
   * Priority for template selection (higher wins when multiple templates match)
   */
  priority: number;

  /**
   * Whether this template matches the given event or message
   */
  matches: (source: SemanticEvent | ChannelMessage) => boolean;

  /**
   * Generate text from the event or message
   */
  generate: (
    source: SemanticEvent | ChannelMessage,
    context: TextGenerationContext
  ) => string | null;
}

/**
 * Output format options for text generation
 */
export interface OutputFormatOptions {
  /**
   * Whether to include HTML formatting tags
   */
  useHtml?: boolean;

  /**
   * Whether to include Markdown formatting
   */
  useMarkdown?: boolean;

  /**
   * CSS class to apply to the main output container (if using HTML)
   */
  containerClass?: string;

  /**
   * Whether to include paragraph tags
   */
  includeParagraphTags?: boolean;

  /**
   * CSS classes to apply to specific message types (if using HTML)
   */
  typeClasses?: Record<string, string>;
}

/**
 * Context information for text generation
 */
export interface TextGenerationContext {
  /**
   * The current game context
   */
  gameContext?: GameContext;

  /**
   * Function to get an entity by ID
   */
  getEntity?: (id: EntityId) => Entity | undefined;

  /**
   * Function to get the name of an entity
   */
  getEntityName?: (id: EntityId) => string;

  /**
   * Output format options
   */
  formatOptions?: OutputFormatOptions;

  /**
   * Additional context data
   */
  [key: string]: any;
}

/**
 * Interface for text generators
 */
export interface TextGenerator {
  /**
   * Register a template for text generation
   */
  registerTemplate: (template: TextTemplate) => void;

  /**
   * Generate text from an event
   */
  generateFromEvent: (
    event: SemanticEvent,
    context?: TextGenerationContext
  ) => string | null;

  /**
   * Generate text from a channel message
   */
  generateFromMessage: (
    message: ChannelMessage,
    context?: TextGenerationContext
  ) => string | null;

  /**
   * Generate text for a group of messages
   */
  generateFromMessages: (
    messages: ChannelMessage[],
    context?: TextGenerationContext
  ) => string;

  /**
   * Generate a full turn response from the latest events/messages
   */
  generateTurnResponse: (
    context?: TextGenerationContext
  ) => string;

  /**
   * Set the output format options
   */
  setFormatOptions: (options: OutputFormatOptions) => void;
}

/**
 * Default text generator implementation
 */
export class DefaultTextGenerator implements TextGenerator {
  private templates: TextTemplate[] = [];
  private eventSource?: EventSource;
  private channelManager?: ChannelManager;
  private formatOptions: OutputFormatOptions = {
    useHtml: false,
    useMarkdown: true,
    includeParagraphTags: true
  };

  /**
   * Create a new text generator
   */
  constructor(
    eventSource?: EventSource,
    channelManager?: ChannelManager,
    formatOptions?: Partial<OutputFormatOptions>
  ) {
    this.eventSource = eventSource;
    this.channelManager = channelManager;

    if (formatOptions) {
      this.formatOptions = {
        ...this.formatOptions,
        ...formatOptions
      };
    }
  }

  /**
   * Register a template for text generation
   */
  public registerTemplate(template: TextTemplate): void {
    this.templates.push(template);

    // Sort templates by priority (highest first)
    this.templates.sort((a, b) => b.priority - a.priority);
  }

  /**
   * Register multiple templates at once
   */
  public registerTemplates(templates: TextTemplate[]): void {
    templates.forEach(template => this.registerTemplate(template));
  }

  /**
   * Set the event source for this text generator
   */
  public setEventSource(eventSource: EventSource): void {
    this.eventSource = eventSource;
  }

  /**
   * Set the channel manager for this text generator
   */
  public setChannelManager(channelManager: ChannelManager): void {
    this.channelManager = channelManager;
  }

  /**
   * Set the output format options
   */
  public setFormatOptions(options: OutputFormatOptions): void {
    this.formatOptions = {
      ...this.formatOptions,
      ...options
    };
  }

  /**
   * Find the best template for an event or message
   */
  private findTemplate(source: SemanticEvent | ChannelMessage): TextTemplate | null {
    // Get source type depending on whether it's an event or message
    const sourceType = 'type' in source ? source.type : 
                      ('content' in source && typeof source.content === 'object' && source.content.type) 
                        ? source.content.type 
                        : 'unknown';

    // Find matching templates
    const matchingTemplates = this.templates.filter(template => 
      (template.type === sourceType || template.type === '*') && 
      template.matches(source)
    );

    // Return the highest priority match, or null if none found
    return matchingTemplates.length > 0 ? matchingTemplates[0] : null;
  }

  /**
   * Generate text from an event
   */
  public generateFromEvent(
    event: SemanticEvent, 
    context: TextGenerationContext = {}
  ): string | null {
    // Find a template that matches this event
    const template = this.findTemplate(event);
    if (!template) return null;

    // Apply the template to generate text
    const text = template.generate(event, {
      ...context,
      formatOptions: context.formatOptions || this.formatOptions
    });

    return text;
  }

  /**
   * Generate text from a channel message
   */
  public generateFromMessage(
    message: ChannelMessage,
    context: TextGenerationContext = {}
  ): string | null {
    // For simple text messages, just return the content
    if (typeof message.content === 'string') {
      return this.formatParagraph(message.content, message.type);
    }

    // Find a template that matches this message
    const template = this.findTemplate(message);
    if (!template) {
      // Fallback for structured content without a template
      if (typeof message.content === 'object' && message.content.text) {
        return this.formatParagraph(message.content.text as string, message.type);
      }
      return null;
    }

    // Apply the template to generate text
    const text = template.generate(message, {
      ...context,
      formatOptions: context.formatOptions || this.formatOptions
    });

    return text;
  }

  /**
   * Generate text for a group of messages
   */
  public generateFromMessages(
    messages: ChannelMessage[],
    context: TextGenerationContext = {}
  ): string {
    const textParts: string[] = [];

    // Generate text for each message
    for (const message of messages) {
      const text = this.generateFromMessage(message, context);
      if (text) {
        textParts.push(text);
      }
    }

    // Join the parts together
    return textParts.join('\n\n');
  }

  /**
   * Generate a full turn response from the latest events/messages
   */
  public generateTurnResponse(context: TextGenerationContext = {}): string {
    if (!this.channelManager) {
      throw new Error('No channel manager set for text generator');
    }

    // Get messages from relevant channels
    const mainMessages = this.channelManager.getMessages('main');
    const inventoryMessages = this.channelManager.getMessages('inventory');
    const systemMessages = this.channelManager.getMessages('system');

    // Generate text for each channel
    const mainText = this.generateFromMessages(mainMessages, context);
    const inventoryText = this.generateFromMessages(inventoryMessages, context);
    const systemText = this.generateFromMessages(systemMessages, context);

    // Combine the texts with appropriate separation
    const parts: string[] = [];

    if (mainText) parts.push(mainText);
    if (inventoryText) parts.push(inventoryText);
    if (systemText) parts.push(systemText);

    return parts.join('\n\n');
  }

  /**
   * Format text as a paragraph
   */
  private formatParagraph(text: string, type: string = 'text'): string {
    if (!text) return '';

    const options = this.formatOptions;

    if (options.useHtml) {
      // Generate HTML with appropriate classes
      const typeClass = options.typeClasses?.[type] || type;

      if (options.includeParagraphTags) {
        return `<p class="${typeClass}">${text}</p>`;
      }

      return `<span class="${typeClass}">${text}</span>`;
    }

    if (options.useMarkdown) {
      // Return simple Markdown format
      return text;
    }

    // Plain text
    return text;
  }
}

/**
 * Create a new text generator
 */
export function createTextGenerator(
  eventSource?: EventSource,
  channelManager?: ChannelManager,
  formatOptions?: Partial<OutputFormatOptions>
): TextGenerator {
  return new DefaultTextGenerator(eventSource, channelManager, formatOptions);
}

// packages/core/src/text-generation/standard-templates.ts

import { SemanticEvent } from '../events/types';
import { ChannelMessage } from '../channels/types';
import { StandardEventTypes } from '../events/standard-events';
import { TextTemplate, TextGenerationContext } from './text-generator';

/**
 * Create a simple template for events of a specific type
 */
function createEventTemplate(
  eventType: string,
  generator: (event: SemanticEvent, context: TextGenerationContext) => string | null,
  options: {
    id?: string;
    priority?: number;
    matcher?: (event: SemanticEvent) => boolean;
  } = {}
): TextTemplate {
  return {
    id: options.id || `template_${eventType}`,
    type: eventType,
    priority: options.priority || 100,
    matches: (source) => {
      if (!('type' in source) || source.type !== eventType) return false;
      return options.matcher ? options.matcher(source as SemanticEvent) : true;
    },
    generate: (source, context) => {
      return generator(source as SemanticEvent, context);
    }
  };
}

/**
 * Create a simple template for messages of a specific type
 */
function createMessageTemplate(
  messageType: string,
  generator: (message: ChannelMessage, context: TextGenerationContext) => string | null,
  options: {
    id?: string;
    priority?: number;
    matcher?: (message: ChannelMessage) => boolean;
  } = {}
): TextTemplate {
  return {
    id: options.id || `template_${messageType}`,
    type: messageType,
    priority: options.priority || 100,
    matches: (source) => {
      if (!('content' in source)) return false;
      if (typeof source.content === 'object' && source.content && 'type' in source.content) {
        return source.content.type === messageType &&
          (options.matcher ? options.matcher(source as ChannelMessage) : true);
      }
      return source.type === messageType &&
        (options.matcher ? options.matcher(source as ChannelMessage) : true);
    },
    generate: (source, context) => {
      return generator(source as ChannelMessage, context);
    }
  };
}

/**
 * Get the entity name from context or use a default
 */
function getEntityName(
  id: string | undefined,
  defaultName: string,
  context: TextGenerationContext
): string {
  if (!id) return defaultName;

  // Use context helper if available
  if (context.getEntityName) {
    return context.getEntityName(id) || defaultName;
  }

  // Use game context if available
  if (context.gameContext) {
    const entity = context.gameContext.getEntity(id);
    if (entity) {
      return String(entity.attributes.name || defaultName);
    }
  }

  // Use getEntity helper if available
  if (context.getEntity) {
    const entity = context.getEntity(id);
    if (entity) {
      return String(entity.attributes.name || defaultName);
    }
  }

  return defaultName;
}

/**
 * Template for PLAYER_MOVED events
 */
export const PLAYER_MOVED_TEMPLATE: TextTemplate = createEventTemplate(
  StandardEventTypes.PLAYER_MOVED,
  (event, context) => {
    const payload = event.payload || {};

    if (payload.arrivalDescription) {
      return String(payload.arrivalDescription);
    }

    const toName = getEntityName(
      event.entities.location,
      payload.toName as string || 'a new location',
      context
    );

    const directionText = payload.direction ? ` to the ${payload.direction}` : '';

    if (payload.enteredObject) {
      return `You enter ${toName}.`;
    }

    if (payload.exitedObject) {
      return `You exit ${payload.fromName || 'the container'} and find yourself in ${toName}.`;
    }

    return `You go${directionText} and arrive at ${toName}.`;
  }
);

