/**
 * Template for ITEM_EXAMINED events (looking at locations)
 */
export const LOCATION_EXAMINED_TEMPLATE: TextTemplate = createEventTemplate(
  StandardEventTypes.ITEM_EXAMINED,
  (event, context) => {
    const payload = event.payload || {};

    // Check if this is a room description (target equals location)
    if (event.entities.target === event.entities.location) {
      const description = payload.description || 'You see nothing special.';

      let text = String(description);

      // Add information about exits if available
      if (payload.exits && typeof payload.exits === 'object') {
        const exits = Object.entries(payload.exits)
          .map(([direction, destination]) => `${direction}: ${destination}`)
          .join(', ');

        if (exits) {
          text += `\n\nExits: ${exits}`;
        }
      }

      // Add information about visible items if available
      if (payload.visibleItems && Array.isArray(payload.visibleItems) && payload.visibleItems.length > 0) {
        const itemNames = payload.visibleItems
          .map(item => item.name || 'unnamed object')
          .join(', ');

        if (itemNames) {
          text += `\n\nYou can see: ${itemNames}`;
        }
      }

      return text;
    }

    return null; // Not a location examination
  },
  {
    id: 'location_examined',
    priority: 110, // Higher priority for room descriptions
    matcher: (event) => event.entities.target === event.entities.location
  }
);

/**
 * Template for ITEM_EXAMINED events (looking at objects)
 */
export const ITEM_EXAMINED_TEMPLATE: TextTemplate = createEventTemplate(
  StandardEventTypes.ITEM_EXAMINED,
  (event, context) => {
    const payload = event.payload || {};

    // Skip location examinations (handled by different template)
    if (event.entities.target === event.entities.location) {
      return null;
    }

    const itemName = payload.itemName || 'the object';
    const description = payload.description || `You see nothing special about ${itemName}.`;

    let text = String(description);

    // Add information about contents if it's an open container
    if (payload.isContainer && payload.isOpen && payload.contents) {
      if (Array.isArray(payload.contents) && payload.contents.length > 0) {
        const contentNames = payload.contents
          .map((item: any) => item.name || 'unnamed object')
          .join(', ');

        if (contentNames) {
          text += `\n\n${itemName} contains: ${contentNames}`;
        } else {
          text += `\n\n${itemName} is empty.`;
        }
      } else {
        text += `\n\n${itemName} is empty.`;
      }
    }

    return text;
  },
  {
    id: 'item_examined',
    matcher: (event) => event.entities.target !== event.entities.location
  }
);

/**
 * Template for ITEM_TAKEN events
 */
export const ITEM_TAKEN_TEMPLATE: TextTemplate = createEventTemplate(
  StandardEventTypes.ITEM_TAKEN,
  (event, context) => {
    const payload = event.payload || {};
    const itemName = payload.itemName || getEntityName(event.entities.target, 'the item', context);

    if (payload.fromContainer) {
      const containerName = payload.containerName || 
        getEntityName(payload.containerId as string, 'the container', context);
      return `You take ${itemName} from ${containerName}.`;
    }

    return `You take ${itemName}.`;
  }
);

