/**
 * Register all standard templates with a text generator
 */
export function registerStandardTemplates(generator: any): void {
  STANDARD_TEMPLATES.forEach(template => generator.registerTemplate(template));
}

// packages/core/src/text-generation/text-formatter.ts

import { OutputFormatOptions } from './text-generator';

/**
 * Formatting options for individual text elements
 */
export interface ElementFormatOptions {
  /**
   * CSS class to apply to the element
   */
  className?: string;

  /**
   * Element type (p, div, span, etc)
   */
  elementType?: string;

  /**
   * Text style (bold, italic, etc)
   */
  style?: 'normal' | 'bold' | 'italic' | 'bolditalic' | 'monospace';

  /**
   * Text color
   */
  color?: string;
}

/**
 * Interface for text formatters
 */
export interface TextFormatter {
  /**
   * Format a paragraph of text
   */
  formatParagraph: (text: string, options?: ElementFormatOptions) => string;

  /**
   * Format a heading
   */
  formatHeading: (text: string, level?: 1 | 2 | 3 | 4 | 5 | 6, options?: ElementFormatOptions) => string;

  /**
   * Format a list of items
   */
  formatList: (items: string[], ordered?: boolean, options?: ElementFormatOptions) => string;

  /**
   * Format an emphasized section of text
   */
  formatEmphasis: (text: string, options?: ElementFormatOptions) => string;

  /**
   * Format a strong section of text
   */
  formatStrong: (text: string, options?: ElementFormatOptions) => string;

  /**
   * Format a code block
   */
  formatCode: (code: string, language?: string, options?: ElementFormatOptions) => string;

  /**
   * Format a block quote
   */
  formatBlockquote: (text: string, options?: ElementFormatOptions) => string;

  /**
   * Format a link
   */
  formatLink: (text: string, url: string, options?: ElementFormatOptions) => string;

  /**
   * Format a horizontal rule
   */
  formatHorizontalRule: (options?: ElementFormatOptions) => string;
}

/**
 * HTML text formatter implementation
 */
export class HtmlTextFormatter implements TextFormatter {
  private options: OutputFormatOptions;

  constructor(options: OutputFormatOptions = {}) {
    this.options = {
      useHtml: true,
      ...options
    };
  }

  /**
   * Create HTML element with options
   */
  private createElement(tag: string, content: string, options?: ElementFormatOptions): string {
    const className = options?.className || '';
    const classAttr = className ? ` class="${className}"` : '';

    const styleArr: string[] = [];

    if (options?.style) {
      switch (options.style) {
        case 'bold':
          styleArr.push('font-weight: bold;');
          break;
        case 'italic':
          styleArr.push('font-style: italic;');
          break;
        case 'bolditalic':
          styleArr.push('font-weight: bold; font-style: italic;');
          break;
        case 'monospace':
          styleArr.push('font-family: monospace;');
          break;
      }
    }

    if (options?.color) {
      styleArr.push(`color: ${options.color};`);
    }

    const styleAttr = styleArr.length > 0 ? ` style="${styleArr.join(' ')}"` : '';

    return `<${tag}${classAttr}${styleAttr}>${content}</${tag}>`;
  }

  public formatParagraph(text: string, options?: ElementFormatOptions): string {
    return this.createElement('p', text, options);
  }

  public formatHeading(text: string, level: 1 | 2 | 3 | 4 | 5 | 6 = 2, options?: ElementFormatOptions): string {
    return this.createElement(`h${level}`, text, options);
  }

  public formatList(items: string[], ordered: boolean = false, options?: ElementFormatOptions): string {
    const tag = ordered ? 'ol' : 'ul';
    const itemElements = items.map(item => `<li>${item}</li>`).join('');
    return this.createElement(tag, itemElements, options);
  }

  public formatEmphasis(text: string, options?: ElementFormatOptions): string {
    return this.createElement('em', text, options);
  }

  public formatStrong(text: string, options?: ElementFormatOptions): string {
    return this.createElement('strong', text, options);
  }

  public formatCode(code: string, language?: string, options?: ElementFormatOptions): string {
    const codeElement = this.createElement('code', code, { 
      ...options,
      className: language ? `language-${language} ${options?.className || ''}` : options?.className
    });
    return this.createElement('pre', codeElement, options);
  }

  public formatBlockquote(text: string, options?: ElementFormatOptions): string {
    return this.createElement('blockquote', text, options);
  }

  public formatLink(text: string, url: string, options?: ElementFormatOptions): string {
    const className = options?.className || '';
    const classAttr = className ? ` class="${className}"` : '';
    return `<a href="${url}"${classAttr}>${text}</a>`;
  }

  public formatHorizontalRule(options?: ElementFormatOptions): string {
    const className = options?.className || '';
    const classAttr = className ? ` class="${className}"` : '';
    return `<hr${classAttr} />`;
  }
}

