// Export version
export const version = '0.1.0';

// Export core modules with explicit re-exports to avoid naming collisions
import * as WorldModel from './world-model';
import * as Parser from './parser';
import * as Execution from './execution';
import * as Events from './events';
import * as Channels from './channels';
import * as Extensions from './extensions';

// Re-export from world-model module but exclude EventEmitter
export {
  Entity,
  EntityId,
  EntityCreationParams,
  EntityOperationOptions,
  AttributeValue,
  AttributeObject,
  AttributeArray,
  StandardAttribute,
  AttributeConfig,
  AttributeConfigMap,
  RelationshipType,
  Relationship,
  RelationshipConfig,
  RelationshipConfigMap,
  STANDARD_RELATIONSHIP_CONFIGS,
  WorldState,
  WorldStateMeta,
  StateTransformer,
  StateHistoryEntry,
  StateManagerConfig,
  EntityPredicate,
  EntityQuery,
  QueryResult,
  QueryOptions,
  WorldModelExtension,
  ExtensionRegistry,
  StateManager,
  EntityManager,
  QueryEngine,
  createStateManager,
  createEntityManager,
  createQueryEngine
} from './world-model';

// Re-export everything from other modules
export * from './parser';
export * from './execution';
export * from './events';
export * from './channels';
export * from './extensions';

// Export namespaced modules for when explicit imports are needed
export { WorldModel, Parser, Execution, Events, Channels, Extensions };

// packages/core/src/events/index.ts

export * from './types';
export * from './standard-events';
export * from './event-system';

// packages/core/src/events/types.ts

import { EntityId } from '../world-model/types';

/**
 * Represents a semantic event in the game
 */
export interface SemanticEvent {
  /**
   * Unique identifier for this event
   */
  id: string;
  
  /**
   * The type of event
   */
  type: string;
  
  /**
   * Timestamp when the event was created
   */
  timestamp: number;
  
  /**
   * Entity IDs relevant to this event
   */
  entities: {
    /**
     * The entity that initiated the event (often the player)
     */
    actor?: EntityId;
    
    /**
     * The primary entity that the event affects
     */
    target?: EntityId;
    
    /**
     * A secondary entity involved in the event
     */
    instrument?: EntityId;
    
    /**
     * A location where the event occurred
     */
    location?: EntityId;
    
    /**
     * Other relevant entities
     */
    others?: EntityId[];
  };
  
  /**
   * Additional data related to the event
   */
  payload?: Record<string, unknown>;
  
  /**
   * Tags for categorizing and filtering events
   */
  tags?: string[];
  
  /**
   * Priority of the event (higher numbers are more important)
   */
  priority?: number;
  
  /**
   * Whether this event should be narrated
   */
  narrate?: boolean;
}

/**
 * Interface for event source that collects and manages events
 */
export interface EventSource {
  /**
   * Add an event to the source
   */
  addEvent: (event: SemanticEvent) => void;
  
  /**
   * Get all events in the source
   */
  getAllEvents: () => SemanticEvent[];
  
  /**
   * Get events of a specific type
   */
  getEventsByType: (type: string) => SemanticEvent[];
  
  /**
   * Get events involving a specific entity
   */
  getEventsByEntity: (entityId: EntityId) => SemanticEvent[];
  
  /**
   * Get events with a specific tag
   */
  getEventsByTag: (tag: string) => SemanticEvent[];
  
  /**
   * Clear all events
   */
  clearEvents: () => void;
  
  /**
   * Apply a filter to the events
   */
  filter: (predicate: (event: SemanticEvent) => boolean) => SemanticEvent[];
  
  /**
   * Get the event emitter associated with this source
   */
  getEmitter: () => EventEmitter;
}

/**
 * Event listener for semantic events
 */
export type EventListener = (event: SemanticEvent) => void;

/**
 * Event emitter for semantic events
 */
export interface EventEmitter {
  /**
   * Add an event listener for a specific event type
   * Use '*' to listen to all events
   */
  on: (type: string, listener: EventListener) => void;
  
  /**
   * Remove an event listener
   */
  off: (type: string, listener: EventListener) => void;
  
  /**
   * Emit an event
   */
  emit: (event: SemanticEvent) => void;
}

/**
 * Configuration options for the event system
 */
export interface EventSystemOptions {
  /**
   * Maximum number of events to store in memory
   */
  maxEvents?: number;
  
  /**
   * Whether to emit events immediately when added
   */
  emitOnAdd?: boolean;
  
  /**
   * Custom filter for events that should be emitted
   */
  emitFilter?: (event: SemanticEvent) => boolean;
}

// packages/core/src/channels/index.ts

export * from './types';
export * from './channel-system';

// packages/core/src/channels/types.ts

import { SemanticEvent } from '../events/types';

/**
 * Represents a message in a channel
 */
export interface ChannelMessage {
  /**
   * Unique identifier for this message
   */
  id: string;
  
  /**
   * Source event(s) that generated this message
   */
  sourceEventIds: string[];
  
  /**
   * When the message was created
   */
  timestamp: number;
  
  /**
   * Content of the message - could be a string or a structured object
   */
  content: string | Record<string, unknown>;
  
  /**
   * Type of the message (for formatting/display purposes)
   */
  type: string;
  
  /**
   * Additional metadata about the message
   */
  metadata?: Record<string, unknown>;
}

/**
 * Definition of a channel
 */
export interface ChannelDefinition {
  /**
   * Unique identifier for this channel
   */
  id: string;
  
  /**
   * Human-readable name of the channel
   */
  name: string;
  
  /**
   * Description of what the channel is used for
   */
  description: string;
  
  /**
   * Event types that this channel handles
   */
  eventTypes: string[];
  
  /**
   * Event tags that this channel handles (optional)
   */
  eventTags?: string[];
  
  /**
   * Custom filter for events
   */
  filter?: (event: SemanticEvent) => boolean;
  
  /**
   * Formatter to convert events to channel messages
   */
  formatter: (event: SemanticEvent) => ChannelMessage | ChannelMessage[] | null;
  
  /**
   * Maximum number of messages to keep in the channel
   */
  maxMessages?: number;
  
  /**
   * Whether this channel is persistent (survives across turns)
   */
  persistent?: boolean;
}

/**
 * A channel that collects and manages messages
 */
export interface Channel {
  /**
   * The channel definition
   */
  definition: ChannelDefinition;
  
  /**
   * Add a message to the channel
   */
  addMessage: (message: ChannelMessage) => void;
  
  /**
   * Process an event into messages
   */
  processEvent: (event: SemanticEvent) => boolean;
  
  /**
   * Get all messages in the channel
   */
  getMessages: () => ChannelMessage[];
  
  /**
   * Get a specific message by ID
   */
  getMessage: (id: string) => ChannelMessage | undefined;
  
  /**
   * Clear all messages in the channel
   */
  clearMessages: () => void;
}

/**
 * Interface for managing channels
 */
export interface ChannelManager {
  /**
   * Register a channel
   */
  registerChannel: (definition: ChannelDefinition) => Channel;
  
  /**
   * Get a channel by ID
   */
  getChannel: (id: string) => Channel | undefined;
  
  /**
   * Get all registered channels
   */
  getAllChannels: () => Channel[];
  
  /**
   * Process an event through all channels
   */
  processEvent: (event: SemanticEvent) => void;
  
  /**
   * Get messages from a specific channel
   */
  getMessages: (channelId: string) => ChannelMessage[];
  
  /**
   * Get messages from all channels, optionally filtered
   */
  getAllMessages: (filter?: (message: ChannelMessage) => boolean) => ChannelMessage[];
}

