// packages/core/src/parser/core/__tests__/tokenizer.test.ts

import { Tokenizer, createTokenizer } from '../tokenizer';
import { TokenType } from '../types';

describe('Tokenizer', () => {
  let tokenizer: Tokenizer;

  beforeEach(() => {
    tokenizer = createTokenizer();
  });

  describe('basic tokenization', () => {
    it('should tokenize a simple command', () => {
      const input = 'take the red key';
      const tokens = tokenizer.tokenize(input);
      
      expect(tokens.length).toBe(4);
      expect(tokens[0].text).toBe('take');
      expect(tokens[0].type).toBe(TokenType.WORD);
      expect(tokens[0].normalized).toBe('take');
      
      expect(tokens[1].text).toBe('the');
      expect(tokens[1].type).toBe(TokenType.WORD);
      
      expect(tokens[3].text).toBe('key');
      expect(tokens[3].type).toBe(TokenType.WORD);
    });

    it('should handle punctuation', () => {
      const input = 'open the door, then go north.';
      const tokens = tokenizer.tokenize(input);
      
      expect(tokens.length).toBe(7);
      expect(tokens[2].text).toBe('door');
      expect(tokens[3].text).toBe(',');
      expect(tokens[3].type).toBe(TokenType.PUNCTUATION);
      expect(tokens[6].text).toBe('.');
      expect(tokens[6].type).toBe(TokenType.PUNCTUATION);
    });

    it('should handle numbers', () => {
      const input = 'turn dial to 42';
      const tokens = tokenizer.tokenize(input);
      
      expect(tokens.length).toBe(4);
      expect(tokens[3].text).toBe('42');
      expect(tokens[3].type).toBe(TokenType.NUMBER);
      expect(tokens[3].normalized).toBe('42');
    });

    it('should handle quoted text', () => {
      const input = 'say "hello there"';
      const tokens = tokenizer.tokenize(input);
      
      expect(tokens.length).toBe(2);
      expect(tokens[1].text).toBe('"hello there"');
      expect(tokens[1].type).toBe(TokenType.QUOTED);
      expect(tokens[1].normalized).toBe('hello there');
    });
  });

  describe('advanced features', () => {
    it('should handle contractions', () => {
      const input = "don't take the lamp";
      const tokens = tokenizer.tokenize(input);
      
      expect(tokens.length).toBe(4);
      expect(tokens[0].text).toBe("don't");
      expect(tokens[0].type).toBe(TokenType.WORD);
    });

    it('should handle hyphenated words', () => {
      const input = 'examine well-crafted sword';
      const tokens = tokenizer.tokenize(input);
      
      expect(tokens.length).toBe(3);
      expect(tokens[1].text).toBe('well-crafted');
      expect(tokens[1].type).toBe(TokenType.WORD);
    });

    it('should include whitespace when configured', () => {
      const tokenizerWithWhitespace = createTokenizer({ includeWhitespace: true });
      const input = 'look  around';
      const tokens = tokenizerWithWhitespace.tokenize(input);
      
      expect(tokens.length).toBe(3);
      expect(tokens[1].text).toBe('  ');
      expect(tokens[1].type).toBe(TokenType.WHITESPACE);
    });

    it('should handle custom patterns', () => {
      const customTokenizer = createTokenizer({
        customPatterns: [
          { pattern: /^#\w+/, type: TokenType.WORD } // Handle hashtags
        ]
      });
      
      const input = 'look at #treasure';
      const tokens = customTokenizer.tokenize(input);
      
      expect(tokens.length).toBe(3);
      expect(tokens[2].text).toBe('#treasure');
      expect(tokens[2].type).toBe(TokenType.WORD);
    });
  });

  describe('edge cases', () => {
    it('should handle empty input', () => {
      const tokens = tokenizer.tokenize('');
      expect(tokens.length).toBe(0);
    });

    it('should handle unknown characters', () => {
      const input = 'look @';
      const tokens = tokenizer.tokenize(input);
      
      expect(tokens.length).toBe(2);
      expect(tokens[1].text).toBe('@');
      expect(tokens[1].type).toBe(TokenType.UNKNOWN);
    });

    it('should handle multiple consecutive punctuations', () => {
      const input = 'wow!!!';
      const tokens = tokenizer.tokenize(input);
      
      expect(tokens.length).toBe(4);
      expect(tokens[0].text).toBe('wow');
      expect(tokens[1].text).toBe('!');
      expect(tokens[2].text).toBe('!');
      expect(tokens[3].text).toBe('!');
    });
  });

  describe('runtime configuration', () => {
    it('should allow adding patterns after creation', () => {
      tokenizer.addPattern(/^@\w+/, TokenType.WORD); // Handle mentions
      
      const input = 'tell @guard about the key';
      const tokens = tokenizer.tokenize(input);
      
      expect(tokens.length).toBe(5);
      expect(tokens[1].text).toBe('@guard');
      expect(tokens[1].type).toBe(TokenType.WORD);
    });

    it('should allow toggling whitespace inclusion', () => {
      tokenizer.setIncludeWhitespace(true);
      
      const input = 'look  north';
      const tokens = tokenizer.tokenize(input);
      
      expect(tokens.length).toBe(3);
      expect(tokens[1].type).toBe(TokenType.WHITESPACE);
      
      tokenizer.setIncludeWhitespace(false);
      const tokens2 = tokenizer.tokenize(input);
      expect(tokens2.length).toBe(2);
    });

    it('should allow toggling normalization', () => {
      tokenizer.setNormalizeTokens(false);
      
      const input = 'Look North';
      const tokens = tokenizer.tokenize(input);
      
      expect(tokens[0].text).toBe('Look');
      expect(tokens[0].normalized).toBeUndefined();
      
      tokenizer.setNormalizeTokens(true);
      const tokens2 = tokenizer.tokenize(input);
      expect(tokens2[0].normalized).toBe('look');
    });
  });
});

// packages/core/src/parser/core/tokenizer.ts

import { Token, TokenType } from './types';

/**
 * Options for configuring the tokenizer behavior
 */
export interface TokenizerOptions {
  /**
   * Whether to include whitespace as tokens
   */
  includeWhitespace?: boolean;
  
  /**
   * Whether to normalize tokens (lowercase, etc.)
   */
  normalizeTokens?: boolean;
  
  /**
   * Custom token patterns to recognize
   */
  customPatterns?: Array<{
    pattern: RegExp;
    type: TokenType;
  }>;
}

/**
 * Default tokenizer options
 */
const DEFAULT_OPTIONS: TokenizerOptions = {
  includeWhitespace: false,
  normalizeTokens: true,
  customPatterns: []
};

/**
 * Core tokenizer implementation for parsing interactive fiction commands
 */
export class Tokenizer {
  private options: TokenizerOptions;
  
  // Core token patterns
  private patterns: Array<{
    pattern: RegExp;
    type: TokenType;
  }>;
  
  /**
   * Creates a new tokenizer with the specified options
   * @param options Configuration options
   */
  constructor(options: TokenizerOptions = {}) {
    this.options = { ...DEFAULT_OPTIONS, ...options };
    
    // Initialize basic patterns
    this.patterns = [
      // Numbers (integers and decimals)
      { pattern: /^\d+(\.\d+)?/, type: TokenType.NUMBER },
      
      // Quoted text
      { pattern: /^"([^"]*)"/, type: TokenType.QUOTED },
      { pattern: /^'([^']*)'/, type: TokenType.QUOTED },
      
      // Punctuation
      { pattern: /^[.,;:!?()[\]{}]/, type: TokenType.PUNCTUATION },
      
      // Whitespace
      { pattern: /^\s+/, type: TokenType.WHITESPACE },
      
      // Words (including hyphenated words and apostrophes)
      { pattern: /^[a-zA-Z]+-?[a-zA-Z]*/, type: TokenType.WORD },
      { pattern: /^[a-zA-Z]+\'[a-zA-Z]+/, type: TokenType.WORD }, // contractions and possessives
    ];
    
    // Add custom patterns if provided
    if (this.options.customPatterns) {
      this.patterns = [...this.options.customPatterns, ...this.patterns];
    }
  }
  
  /**
   * Tokenizes an input string into an array of tokens
   * @param input The input text to tokenize
   * @returns Array of tokens
   */
  public tokenize(input: string): Token[] {
    const tokens: Token[] = [];
    let remaining = input.trim();
    let position = 0;
    
    while (remaining.length > 0) {
      let matched = false;
      
      // Try each pattern until one matches
      for (const { pattern, type } of this.patterns) {
        const match = remaining.match(pattern);
        
        if (match && match.index === 0) {
          const text = match[0];
          
          // Skip whitespace tokens if not explicitly included
          if (type === TokenType.WHITESPACE && !this.options.includeWhitespace) {
            remaining = remaining.substring(text.length);
            position += text.length;
            matched = true;
            break;
          }
          
          // Create the token
          const token: Token = {
            text,
            type,
            position
          };
          
          // Add normalized form if requested
          if (this.options.normalizeTokens) {
            token.normalized = this.normalizeToken(text, type);
          }
          
          tokens.push(token);
          
          // Update the remaining text and position
          remaining = remaining.substring(text.length);
          position += text.length;
          matched = true;
          break;
        }
      }
      
      // If no pattern matched, treat the next character as unknown
      if (!matched) {
        tokens.push({
          text: remaining[0],
          type: TokenType.UNKNOWN,
          position
        });
        
        remaining = remaining.substring(1);
        position += 1;
      }
    }
    
    return this.postProcessTokens(tokens);
  }
  
  /**
   * Perform any post-processing on the tokenized results
   * @param tokens The initial tokens
   * @returns The processed tokens
   */
  private postProcessTokens(tokens: Token[]): Token[] {
    // Handle special case: contractions with apostrophes
    // For example, "don't" -> ["don", "'", "t"] should be combined into ["don't"]
    const processedTokens: Token[] = [];
    
    for (let i = 0; i < tokens.length; i++) {
      const token = tokens[i];
      
      // If it's a word token and there's an apostrophe and another word after it
      if (token.type === TokenType.WORD && 
          i + 2 < tokens.length && 
          tokens[i+1].text === "'" && 
          tokens[i+2].type === TokenType.WORD) {
        
        // Combine them into one token
        const combinedToken: Token = {
          text: token.text + "'" + tokens[i+2].text,
          type: TokenType.WORD,
          position: token.position
        };
        
        if (this.options.normalizeTokens) {
          combinedToken.normalized = this.normalizeToken(
            combinedToken.text, 
            TokenType.WORD
          );
        }
        
        processedTokens.push(combinedToken);
        i += 2; // Skip the next two tokens
      } else {
        processedTokens.push(token);
      }
    }
    
    return processedTokens;
  }
  
  /**
   * Normalize a token based on its type
   * @param text The token text
   * @param type The token type
   * @returns The normalized form of the token
   */
  private normalizeToken(text: string, type: TokenType): string {
    switch (type) {
      case TokenType.WORD:
        // Lowercase words
        return text.toLowerCase();
        
      case TokenType.QUOTED:
        // Remove the quotes and trim whitespace
        return text.substring(1, text.length - 1).trim();
        
      case TokenType.NUMBER:
        // Ensure consistent format for numbers
        return parseFloat(text).toString();
        
      default:
        // No normalization for other types
        return text;
    }
  }
  
  /**
   * Add a custom pattern to the tokenizer
   * @param pattern The regular expression to match
   * @param type The token type to assign
   */
  public addPattern(pattern: RegExp, type: TokenType): void {
    // Add to the beginning of the patterns array to give it priority
    this.patterns.unshift({ pattern, type });
  }
  
  /**
   * Set whether to include whitespace tokens
   * @param include Whether to include whitespace
   */
  public setIncludeWhitespace(include: boolean): void {
    this.options.includeWhitespace = include;
  }
  
  /**
   * Set whether to normalize tokens
   * @param normalize Whether to normalize tokens
   */
  public setNormalizeTokens(normalize: boolean): void {
    this.options.normalizeTokens = normalize;
  }
}

/**
 * Create a tokenizer with the specified options
 * @param options Configuration options
 * @returns A new tokenizer instance
 */
export function createTokenizer(options?: TokenizerOptions): Tokenizer {
  return new Tokenizer(options);
}

// packages/core/src/parser/core/parser.ts

import { ParsedCommand, ParsingResult } from './types';

/**
 * Context information for parsing
 */
/**
   * Parsing context containing information about the game state
   */
  export interface ParsingContext {
    /**
     * Last mentioned singular object for pronoun resolution
     */
    lastMentionedSingular?: string;
    
    /**
     * Last mentioned plural objects for pronoun resolution
     */
    lastMentionedPlural?: string;
    
    /**
     * Current location name
     */
    currentLocation?: string;
    
    /**
     * Objects currently accessible to the player
     */
    accessibleObjects?: string[];
    
    /**
     * Objects in the player's inventory
     */
    inventory?: string[];
    
    /**
     * Any other contextual information needed for parsing
     */
    [key: string]: any;
  }

/**
 * Custom vocabulary for the parser
 */
export interface Vocabulary {
  /**
   * Custom verbs to add to the parser
   */
  verbs?: string[];
  
  /**
   * Custom nouns to add to the parser
   */
  nouns?: string[];
  
  /**
   * Custom adjectives to add to the parser
   */
  adjectives?: string[];
  
  /**
   * Custom adverbs to add to the parser
   */
  adverbs?: string[];
  
  /**
   * Custom synonym mappings
   */
  synonyms?: Record<string, string[]>;
}

/**
 * Interface for a language parser
 */
export interface Parser {
  /**
   * Parse text input into a structured command
   * @param input The input text to parse
   * @param context Optional parsing context
   * @returns A parsing result with the command or error information
   */
  parse(input: string, context?: ParsingContext): ParsingResult;
  
  /**
   * Add custom vocabulary to the parser
   * @param vocabulary The vocabulary to add
   */
