      return {
        verbs: { ...IF_VERBS },
        nouns: { ...COMMON_IF_NOUNS },
        adjectives: { ...COMMON_ADJECTIVES },
        adverbs: { ...ADVERBS },
        prepositions: { ...PREPOSITIONS },
        articles: { ...ARTICLES },
        pronouns: { ...PRONOUNS },
        conjunctions: { ...CONJUNCTIONS },
        determiners: { ...DETERMINERS }
