    pattern: /^came$/,
    replacement: 'come',
    appliesTo: [PartOfSpeech.VERB]
  },
  {
    pattern: /^put$/,
    replacement: 'put', // Same form for past and present
    appliesTo: [PartOfSpeech.VERB]
  },
  {
    pattern: /^got$/,
    replacement: 'get',
    appliesTo: [PartOfSpeech.VERB]
  },
  {
    pattern: /^bought$/,
    replacement: 'buy',
    appliesTo: [PartOfSpeech.VERB]
  },
  {
    pattern: /^brought$/,
    replacement: 'bring',
    appliesTo: [PartOfSpeech.VERB]
  },
  {
    pattern: /^caught$/,
    replacement: 'catch',
    appliesTo: [PartOfSpeech.VERB]
  },
  {
    pattern: /^thought$/,
    replacement: 'think',
    appliesTo: [PartOfSpeech.VERB]
  },
  {
    pattern: /^felt$/,
    replacement: 'feel',
    appliesTo: [PartOfSpeech.VERB]
  },
