          finalizePhrase();
          currentType = PhraseType.VERB_PHRASE;
          currentPhrase.push(word);
  
          // Include adverbs immediately following the verb
          let j = i + 1;
          while (j < words.length && words[j].partOfSpeech === PartOfSpeech.ADVERB) {
            currentPhrase.push(words[j]);
            j++;
