## 6. Open/Close Handler

```typescript
// packages/stdlib/src/handlers/open-close-handler.ts

import { ParsedCommand } from '@core/parser/core/types';
import { Entity, EntityId, RelationshipType } from '@core/world-model/types';
import { CommandResult, GameContext } from '@core/execution/types';
import { BaseCommandHandler } from '@core/execution/command-handler';
import { createEvent } from '@core/events/event-system';
import { StandardEventTypes, StandardEventTags } from '@core/events/standard-events';

/**
 * Handler for open and close commands
 */
export class OpenCloseHandler extends BaseCommandHandler {
  constructor() {
    super(['open', 'close', 'shut']);
  }

  /**
   * Execute open/close commands
   */
  public execute(command: ParsedCommand, context: GameContext): CommandResult {
    const { player, currentLocation } = context;
    const isOpen = command.verb.toLowerCase() === 'open';

    // Find the target entity
    const targetEntity = context.findEntityByName(command.directObject || '', {
      location: currentLocation.id,
      includeInventory: true
    });

    if (!targetEntity) {
      return this.createFailureResult(`You don't see any ${command.directObject} here.`);
    }

    // Check if the entity can be opened/closed
    if (targetEntity.attributes.openable !== true) {
      return this.createFailureResult(`You can't ${command.verb} the ${targetEntity.attributes.name || command.directObject}.`);
    }

    // Check if entity is already in the target state
    if (targetEntity.attributes.open === isOpen) {
      return this.createFailureResult(
        `The ${targetEntity.attributes.name || command.directObject} is already ${isOpen ? 'open' : 'closed'}.`
      );
    }

    // Check if the entity is locked (for opening)
    if (isOpen && targetEntity.attributes.locked === true) {
      return this.createFailureResult(
        `The ${targetEntity.attributes.name || command.directObject} is locked.`
      );
    }

    // Update world state - set open/closed state
    const newContext = context.updateWorldState(state => {
      const updatedEntity = { 
        ...state.entities[targetEntity.id],
        attributes: {
          ...state.entities[targetEntity.id].attributes,
          open: isOpen
        }
      };

      // If this is a door or passage, update accessibility
      if (targetEntity.type === 'door' || targetEntity.type === 'passage') {
        updatedEntity.attributes.accessible = isOpen;
      }

      // Update state
      return {
        ...state,
        entities: {
          ...state.entities,
          [targetEntity.id]: updatedEntity
        }
      };
    });

    // Create open/close event
    const eventType = isOpen ? StandardEventTypes.ITEM_OPENED : StandardEventTypes.ITEM_CLOSED;
    const openCloseEvent = createEvent(
      eventType,
      {
        itemName: targetEntity.attributes.name || command.directObject,
        itemId: targetEntity.id,
        itemType: targetEntity.type
      },
      {
        actor: player.id,
        target: targetEntity.id,
        location: currentLocation.id,
        tags: [StandardEventTags.VISIBLE]
      }
    );

    // Return success with event
    return this.createSuccessResult([openCloseEvent]);
  }

  /**
   * Validate open/close command
   */
  public validate(command: ParsedCommand, context: GameContext): { valid: boolean; error?: string } {
    if (!command.directObject) {
      return {
        valid: false,
        error: `What do you want to ${command.verb}?`
      };
    }

    return { valid: true };
  }
}

/**
 * Create a new open/close handler
 */
export function createOpenCloseHandler(): OpenCloseHandler {
  return new OpenCloseHandler();
}
