```typescript
// packages/stdlib/src/handlers/hang-handler.ts

import { ParsedCommand } from '@core/parser/core/types';
import { Entity, EntityId, RelationshipType } from '@core/world-model/types';
import { CommandResult, GameContext } from '@core/execution/types';
import { BaseCommandHandler } from '@core/execution/command-handler';
import { createEvent } from '@core/events/event-system';
import { StandardEventTypes, StandardEventTags } from '@core/events/standard-events';

/**
 * Handler for the "hang" command (specifically for Cloak of Darkness)
 */
export class HangHandler extends BaseCommandHandler {
  constructor() {
    super(['hang', 'put', 'place', 'hook']);
  }

  /**
   * Check if this handler can handle the command
   */
  public canHandle(command: ParsedCommand, context: GameContext): boolean {
    // Handle "hang cloak" or "put cloak on hook" style commands
    const directObject = command.directObject?.toLowerCase();
    const preposition = Object.keys(command.prepositions)[0]?.toLowerCase();
    const indirectObject = preposition ? command.prepositions[preposition]?.toLowerCase() : null;

    return (
      // "hang cloak"
