import { ParsedCommand } from '@core/parser/core/types';
import { Entity, EntityId, RelationshipType } from '@core/world-model/types';
import { CommandResult, GameContext } from '@core/execution/types';
import { BaseCommandHandler } from '@core/execution/command-handler';
import { createEvent } from '@core/events/event-system';
import { StandardEventTypes, StandardEventTags } from '@core/events/standard-events';

/**
 * Handler for drop commands
 */
export class DropHandler extends BaseCommandHandler {
  constructor() {
    super(['drop', 'put', 'place', 'leave']);
  }

  /**
   * Execute the drop command
   */
  public execute(command: ParsedCommand, context: GameContext): CommandResult {
    // Handle special case "drop all"
    if (command.directObject === 'all') {
      return this.dropAll(context);
    }

    const { player, currentLocation } = context;

    // Find the target entity in player's inventory
    const targetEntity = context.findEntityByName(command.directObject || '', {
      includeInventory: true,
      typeFilter: ['item']
    });

    if (!targetEntity) {
      return this.createFailureResult(`You don't have any ${command.directObject}.`);
    }

    // Check if the entity is in player's inventory
    const playerInventory = player.relationships[RelationshipType.CONTAINS] || [];
    if (!playerInventory.includes(targetEntity.id)) {
      return this.createFailureResult(`You don't have the ${targetEntity.attributes.name || command.directObject}.`);
    }

    // Update world state - move entity from inventory to current location
    const newContext = context.updateWorldState(state => {
      // Remove from player inventory
      const playerEntity = { ...state.entities[player.id] };
      playerEntity.relationships = {
        ...playerEntity.relationships,
        [RelationshipType.CONTAINS]: 
          (playerEntity.relationships[RelationshipType.CONTAINS] || [])
          .filter(id => id !== targetEntity.id)
      };

      // Add to current location
      const location = { ...state.entities[currentLocation.id] };
      location.relationships = {
        ...location.relationships,
        [RelationshipType.CONTAINS]: [
          ...(location.relationships[RelationshipType.CONTAINS] || []),
          targetEntity.id
        ]
      };

      // Update state
      return {
        ...state,
        entities: {
          ...state.entities,
          [player.id]: playerEntity,
          [currentLocation.id]: location
        }
      };
    });

    // Create drop event
    const dropEvent = createEvent(
      StandardEventTypes.ITEM_DROPPED,
      {
        itemName: targetEntity.attributes.name || command.directObject,
        itemId: targetEntity.id
      },
      {
        actor: player.id,
        target: targetEntity.id,
        location: currentLocation.id,
        tags: [StandardEventTags.VISIBLE]
      }
    );

    // Return success with event
    return this.createSuccessResult([dropEvent]);
  }

  /**
   * Drop all items in inventory
   */
  private dropAll(context: GameContext): CommandResult {
    const { player } = context;

    // Get all entities in player's inventory
    const inventoryIds = player.relationships[RelationshipType.CONTAINS] || [];

    if (inventoryIds.length === 0) {
      return this.createFailureResult(`You're not carrying anything.`);
    }

    // Drop each entity
    let newContext = context;
    const events = [];

    for (const entityId of inventoryIds) {
      const entity = context.getEntity(entityId);
      if (!entity) continue;

      // Create a fake command for each entity
      const fakeCommand = {
        verb: 'drop',
        directObject: entity.attributes.name as string,
        originalText: `drop ${entity.attributes.name}`
      };

      // Execute the command in the updated context
      const result = this.execute(fakeCommand, newContext);

      if (result.success) {
        // Update context for next iteration
        newContext = result.context || newContext;
        events.push(...result.events);
      }
    }

    if (events.length === 0) {
      return this.createFailureResult(`You couldn't drop anything.`);
    }

    // Return success with all events
    return this.createSuccessResult(events, newContext);
  }

  /**
   * Validate drop command
   */
  public validate(command: ParsedCommand, context: GameContext): { valid: boolean; error?: string } {
    if (!command.directObject) {
      return {
        valid: false,
        error: 'What do you want to drop?'
      };
    }

    return { valid: true };
  }
}

/**
 * Create a new drop handler
 */
export function createDropHandler(): DropHandler {
  return new DropHandler();
}
