import { ParsedCommand } from '@core/parser/core/types';
import { Entity, EntityId, RelationshipType } from '@core/world-model/types';
import { CommandResult, GameContext } from '@core/execution/types';
import { BaseCommandHandler } from '@core/execution/command-handler';
import { createEvent } from '@core/events/event-system';
import { StandardEventTypes, StandardEventTags } from '@core/events/standard-events';

/**
 * Handler for inventory commands
 */
export class InventoryHandler extends BaseCommandHandler {
  constructor() {
    super(['inventory', 'i', 'inv']);
  }

  /**
   * Execute the inventory command
   */
  public execute(command: ParsedCommand, context: GameContext): CommandResult {
    const { player } = context;

    // Get items in inventory
    const inventoryIds = player.relationships[RelationshipType.CONTAINS] || [];
    const inventoryItems = inventoryIds
      .map(id => context.getEntity(id))
      .filter((e): e is Entity => !!e);

    // Create inventory event
    const inventoryEvent = createEvent(
      StandardEventTypes.INVENTORY_CHECKED,
      {
        items: inventoryItems.map(item => ({
          id: item.id,
          name: item.attributes.name,
          type: item.type
        })),
        isEmpty: inventoryItems.length === 0
      },
      {
        actor: player.id,
        tags: [StandardEventTags.VISIBLE]
      }
    );

    // Return success with event
    return this.createSuccessResult([inventoryEvent]);
  }
}

/**
 * Create a new inventory handler
 */
export function createInventoryHandler(): InventoryHandler {
  return new InventoryHandler();
}
