/**
 * Handler for open commands
 */
export class OpenHandler extends BaseCommandHandler {
  constructor() {
    super(['open', 'unseal', 'unlock']);
  }

  /**
   * Execute the open command
   */
  public execute(command: ParsedCommand, context: GameContext): CommandResult {
    const { player, currentLocation } = context;

    // Find the target entity
    const targetEntity = context.findEntityByName(command.directObject || '', {
      location: currentLocation.id,
      includeInventory: true
    });

    if (!targetEntity) {
      return this.createFailureResult(`You don't see any ${command.directObject} here.`);
    }

    // Check if the entity can be opened
    if (targetEntity.attributes.openable !== true) {
      return this.createFailureResult(`You can't open the ${targetEntity.attributes.name || command.directObject}.`);
    }

    // Check if already open
    if (targetEntity.attributes.open === true) {
      return this.createFailureResult(
        `The ${targetEntity.attributes.name || command.directObject} is already open.`
      );
    }

    // Check if locked
    if (targetEntity.attributes.locked === true) {
      return this.createFailureResult(
        `The ${targetEntity.attributes.name || command.directObject} is locked.`
      );
    }

    // Update world state - set to open
    const newContext = context.updateWorldState(state => {
      const updatedEntity = { 
        ...state.entities[targetEntity.id],
        attributes: {
          ...state.entities[targetEntity.id].attributes,
          open: true
        }
      };

      // If this is a door or passage, update accessibility
      if (targetEntity.type === 'door' || targetEntity.type === 'passage') {
        updatedEntity.attributes.accessible = true;
      }

      // Update state
      return {
        ...state,
        entities: {
          ...state.entities,
          [targetEntity.id]: updatedEntity
        }
      };
    });

    // Create open event
    const openEvent = createEvent(
      StandardEventTypes.ITEM_OPENED,
      {
        itemName: targetEntity.attributes.name || command.directObject,
        itemId: targetEntity.id,
        itemType: targetEntity.type
      },
      {
        actor: player.id,
        target: targetEntity.id,
        location: currentLocation.id,
        tags: [StandardEventTags.VISIBLE]
      }
    );

    // Return success with event
    return this.createSuccessResult([openEvent]);
  }

  /**
   * Validate open command
   */
  public validate(command: ParsedCommand, context: GameContext): { valid: boolean; error?: string } {
    if (!command.directObject) {
      return {
        valid: false,
        error: `What do you want to open?`
      };
    }

    return { valid: true };
  }
}

/**
 * Create a new open handler
 */
export function createOpenHandler(): OpenHandler {
  return new OpenHandler();
}
