    // Only include general movement verbs, not direction words
    super(['go', 'move', 'walk', 'run', 'head', 'travel']);

    // Handle both system object and system name
    if (typeof movementSystem === 'string') {
      // Find the system by name
      const systemKey = Object.keys(MOVEMENT_SYSTEMS).find(
        key => MOVEMENT_SYSTEMS[key].name.toLowerCase() === movementSystem.toLowerCase()
      ) || 'COMPASS';
      this.activeMovementSystem = MOVEMENT_SYSTEMS[systemKey];
    } else {
