// packages/stdlib/src/handlers/hang-handler.ts

import { ParsedCommand } from '@sharpee/core';
import { Entity, EntityId, RelationshipType } from '@sharpee/core';
import { CommandResult, GameContext } from '@sharpee/core';
import { BaseCommandHandler } from '@sharpee/core';
import { createEvent } from '@sharpee/core';
import { StandardEventTypes, StandardEventTags } from '@sharpee/core';

/**
 * Handler for the "hang" command (specifically for Cloak of Darkness)
 */
export class HangHandler extends BaseCommandHandler {
  constructor() {
    super(['hang', 'hook', 'place']);
  }

  /**
   * Check if this handler can handle the command
   */
  public canHandle(command: ParsedCommand, context: GameContext): boolean {
    // Handle "hang cloak" or "put cloak on hook" style commands
    const directObject = command.directObject?.toLowerCase();
    const preposition = Object.keys(command.prepositions)[0]?.toLowerCase();
    const indirectObject = preposition ? command.prepositions[preposition]?.toLowerCase() : null;

    return (
      // "hang cloak"
      (this.verbs.includes(command.verb.toLowerCase()) && directObject === 'cloak') ||
      // "put cloak on hook"
      (command.verb.toLowerCase() === 'put' && 
       directObject === 'cloak' && 
       (preposition === 'on' || preposition === 'upon') && 
       (indirectObject === 'hook' || indirectObject === 'peg' || indirectObject === 'hanger'))
    );
  }

  /**
   * Execute the hang command
   */
  public execute(command: ParsedCommand, context: GameContext): CommandResult {
    const { player, currentLocation } = context;

    // Check if in the cloakroom
    if (currentLocation.attributes.name !== 'Cloakroom') {
      return this.createFailureResult('There\'s nowhere to hang your cloak here.');
    }

    // Find the cloak in player's inventory
    const cloak = context.findEntityByName('cloak', {
      includeInventory: true
    });

    if (!cloak) {
      return this.createFailureResult('You\'re not carrying a cloak.');
    }

    // Find the hook in the current location
    const hook = context.findEntityByName('hook', {
      location: currentLocation.id
    }) || context.findEntityByName('peg', {
      location: currentLocation.id
    }) || context.findEntityByName('hanger', {
      location: currentLocation.id
    });

    if (!hook) {
      return this.createFailureResult('There\'s nothing to hang your cloak on.');
    }

    // Update world state - move cloak to hook and set bar to be lit
    const newContext = context.updateWorldState(state => {
      // Remove cloak from player inventory
      const playerEntity = { ...state.entities[player.id] };
      playerEntity.relationships = {
        ...playerEntity.relationships,
        [RelationshipType.CONTAINS]: 
          (playerEntity.relationships[RelationshipType.CONTAINS] || [])
          .filter(id => id !== cloak.id)
      };

      // Add cloak to hook
      const hookEntity = { ...state.entities[hook.id] };
      hookEntity.relationships = {
        ...hookEntity.relationships,
        [RelationshipType.SUPPORTS]: [
          ...(hookEntity.relationships[RelationshipType.SUPPORTS] || []),
          cloak.id
        ]
      };

      // Mark cloak as hung
      const cloakEntity = { 
        ...state.entities[cloak.id],
        attributes: {
          ...state.entities[cloak.id].attributes,
          hung: true
        }
      };

      // Find the bar room and update it to be lit
      const barRoomId = Object.values(state.entities)
        .find(e => e.type === 'room' && e.attributes.name === 'Bar')?.id;

      let updatedEntities = {
        ...state.entities,
        [player.id]: playerEntity,
        [hook.id]: hookEntity,
        [cloak.id]: cloakEntity
      };

      // Update the bar room if found
      if (barRoomId) {
        updatedEntities = {
          ...updatedEntities,
          [barRoomId]: {
            ...state.entities[barRoomId],
            attributes: {
              ...state.entities[barRoomId].attributes,
              dark: false,
              description: "The bar, now brightly lit, is empty. There's a single message scrawled on the sawdust-covered floor."
            }
          }
        };

        // Find the message in the bar and make it visible
        const messageId = Object.values(state.entities)
          .find(e => e.type === 'item' && e.attributes.name === 'message')?.id;

        if (messageId) {
          updatedEntities = {
            ...updatedEntities,
            [messageId]: {
              ...state.entities[messageId],
              attributes: {
                ...state.entities[messageId].attributes,
                visible: true
              }
            }
          };
        }
      }

      // Update state
      return {
        ...state,
        entities: updatedEntities
      };
    });

    // Create hang event
    const hangEvent = createEvent(
      StandardEventTypes.ITEM_USED,
      {
        itemName: cloak.attributes.name as string,
        itemId: cloak.id,
        targetName: hook.attributes.name as string,
        targetId: hook.id,
        action: 'hang'
      },
      {
        actor: player.id,
        target: cloak.id,
        instrument: hook.id,
        location: currentLocation.id,
        tags: [StandardEventTags.VISIBLE]
      }
    );

    // Return success with event
    return this.createSuccessResult([hangEvent]);
  }
}

/**
 * Create a new hang handler
 */
export function createHangHandler(): HangHandler {
  return new HangHandler();
}

// packages/stdlib/src/handlers/index.ts

// Export look handler
export * from './look-handler';

// Export movement handler
export * from './movement-handler';

// Export take handler
export * from './take-handler';

// Export drop handler
export * from './drop-handler';

// Export inventory handler
export * from './inventory-handler';

// Export wait handler
export * from './wait-handler';

// Export open handler
export * from './open-handler';

// Export close handler
export * from './close-handler';

// Export hang handler (Cloak of Darkness specific)
export * from './hang-handler';

// Factory function to create all standard handlers
export function createStandardHandlers() {
  const { createLookHandler } = require('./look-handler');
  const { createMovementHandler } = require('./movement-handler');
  const { createTakeHandler } = require('./take-handler');
  const { createDropHandler } = require('./drop-handler');
  const { createInventoryHandler } = require('./inventory-handler');
  const { createWaitHandler } = require('./wait-handler');
  const { createOpenHandler } = require('./open-handler');
  const { createCloseHandler } = require('./close-handler');
  const { createHangHandler } = require('./hang-handler');

  return [
    createLookHandler(),
    createMovementHandler(),
    createTakeHandler(),
    createDropHandler(),
    createInventoryHandler(),
    createWaitHandler(),
    createOpenHandler(),
    createCloseHandler(),
    createHangHandler()
  ];
}

