  ruleSystem.rule('weather-hazards')
    .on('command:move:started')
    .when(ctx => ctx.gameContext.worldState.globalState?.weather === 'stormy')
    .after(ctx => {
      // 20% chance of being struck by lightning in stormy weather
      if (Math.random() < 0.2) {
        const currentHealth = ctx.gameContext.player.attributes.health || 0;

        const updatedContext = ctx.gameContext.updateWorldState(state => {
          const playerEntity = { ...state.entities[ctx.player] };
          playerEntity.attributes = {
            ...playerEntity.attributes,
            health: Math.max(0, currentHealth - 3)
          };

          return {
            ...state,
            entities: {
              ...state.entities,
              [ctx.player]: playerEntity
            }
          };
        });

        return ctx.say('RULE_LIGHTNING_STRIKE');
      }

      return ctx;
    });

  // Rule 15: Dynamic NPC interactions based on player reputation
  ruleSystem.rule('npc-reactions')
    .on('command:talk:started')
    .after(ctx => {
      const reputation = ctx.gameContext.player.attributes.reputation || 0;
      const npcId = ctx.data.npcId;

      if (!npcId) return ctx;

      const npc = ctx.gameContext.getEntity(npcId);
      if (!npc) return ctx;

      const npcName = npc.attributes.name as string;

      if (reputation < -50) {
        return ctx.say('RULE_NPC_HOSTILE', [npcName]);
      } else if (reputation < 0) {
        return ctx.say('RULE_NPC_SUSPICIOUS', [npcName]);
      } else if (reputation < 50) {
        return ctx.say('RULE_NPC_NEUTRAL', [npcName]);
      } else {
        return ctx.say('RULE_NPC_FRIENDLY', [npcName]);
      }
    });

