/**
 * Core parser types for Sharpee IF Engine
 */

/**
 * Represents a token in the input text
 */
export interface Token {
  type: TokenType;
  value: string;
  position: number;
  length: number;
}

/**
 * Supported token types
 */
export enum TokenType {
  WORD,
  NUMBER,
  PUNCTUATION,
  SPECIAL_CHAR,
  WHITESPACE,
  UNKNOWN
}

/**
 * Part of Speech tag types
 */
export enum POSType {
  NOUN,
  VERB,
  ADJECTIVE,
  ADVERB,
  PREPOSITION,
  CONJUNCTION,
  ARTICLE,
  PRONOUN,
  NUMBER,
  UNKNOWN
}

/**
 * A word with its assigned POS tag
 */
export interface TaggedWord {
  word: string;
  tag: POSType;
  lemma?: string; // Optional lemmatized form
}

/**
 * Phrase structure in a sentence
 */
export interface Phrase {
  type: PhraseType;
  words: TaggedWord[];
  start: number;
  end: number;
}

/**
 * Types of phrases
 */
export enum PhraseType {
  VERB_PHRASE,
  NOUN_PHRASE,
  PREPOSITIONAL_PHRASE,
  UNKNOWN
}

/**
 * A parsed command with structured components
 */
export interface ParsedCommand {
  verb: string;           // The canonical verb
  directObject?: string;  // The direct object (if any)
  indirectObject?: string; // The indirect object (if any)
  preposition?: string;   // The preposition (if any)
  adjectives: string[];   // List of adjectives modifying objects
  originalText: string;   // The original input text
}

/**
 * Parser error information
 */
export interface ParserError {
  message: string;
  position?: number;
  expected?: string[];
}

/**
 * Result of parsing an input
 */
export interface ParserResult {
  success: boolean;
  commands: ParsedCommand[];
  errors: ParserError[];
}

// packages/core/src/world-model/types/entity.ts

/**
 * Unique identifier for entities in the world
 */
export type EntityId = string;

/**
 * The base Entity interface representing any object in the world model
 */
export interface Entity {
  /**
   * Unique identifier for this entity
   */
  id: EntityId;
  
  /**
   * The entity type, used for categorization and type checking
   */
  type: string;
  
  /**
   * Arbitrary attributes/properties of the entity
   */
  attributes: Record<string, unknown>;
  
  /**
   * Relationships to other entities, organized by relationship type
   */
  relationships: Record<string, EntityId[]>;
}

/**
 * Minimal information needed to create a new entity
 */
export interface EntityCreationParams {
  type: string;
  attributes?: Record<string, unknown>;
  relationships?: Record<string, EntityId[]>;
}

/**
 * Configuration for how entity operations should be performed
 */
export interface EntityOperationOptions {
  /**
   * Whether to merge arrays in relationships instead of replacing them
   */
  mergeRelationships?: boolean;
  
  /**
   * Whether to validate relationship target existence
   */
  validateRelationships?: boolean;
}

// packages/stdlib/src/handlers/take-handler.ts

import {
  ParsedCommand,
  Entity,
  EntityId,
  RelationshipType,
  CommandResult,
  GameContext,
  BaseCommandHandler,
  createEvent,
  StandardEventTypes,
  StandardEventTags
} from '../core-imports';

/**
 * Handler for take/get commands
 */
export class TakeHandler extends BaseCommandHandler {
  constructor() {
    super(['take', 'get', 'pick', 'grab', 'acquire']);
  }

  /**
   * Execute the take command
   */
  public async execute(command: ParsedCommand, context: GameContext): Promise<CommandResult> {
    // Handle special cases like "take all" or "take inventory"
    if (command.directObject === 'all') {
      return this.takeAll(context);
    }

    if (command.directObject === 'inventory' || command.directObject === 'inv') {
      // Redirect to inventory command
      const invCommand: ParsedCommand = { 
        verb: 'inventory', 
        directObject: undefined, 
        raw: 'inventory',
        originalText: 'inventory',
        qualifiers: {},
        prepositions: {}
      } as ParsedCommand;

      // Find the inventory handler
      const invHandler = context.commandRouter?.getHandler(invCommand, context);
      if (invHandler) {
        // Return the handler's result directly, whether it's a Promise or not
        return invHandler.execute(invCommand, context);
      }
    }

    // Normal case - taking a specific object
    const { player, currentLocation } = context;

    // Find the target entity
    const targetEntity = context.findEntityByName(command.directObject || '', {
      location: currentLocation.id,
      includeInventory: false
    });

    if (!targetEntity) {
      return this.createFailureResult('objectNotFound', command.directObject);
    }

    // Check if the entity is takeable
    if (targetEntity.attributes.takeable === false) {
      return this.createFailureResult('notTakeable', targetEntity.attributes.name as string || command.directObject);
    }

    // Check if the entity is accessible
    if (!context.isAccessible(targetEntity.id)) {
      return this.createFailureResult('notAccessible', targetEntity.attributes.name as string || command.directObject);
    }

    // Check if the entity is already in inventory
    const playerInventory = player.relationships[RelationshipType.CONTAINS] || [];
    if (playerInventory.includes(targetEntity.id)) {
      return this.createFailureResult('alreadyInInventory', targetEntity.attributes.name as string || command.directObject);
    }

    // Update world state - move entity to player's inventory
    const updatedContext = context.updateWorldState(state => {
      // Remove from current container
      const container = { ...state.entities[currentLocation.id] };
      container.relationships = {
        ...container.relationships,
        [RelationshipType.CONTAINS]: 
          (container.relationships[RelationshipType.CONTAINS] || [])
          .filter(id => id !== targetEntity.id)
      };

      // Add to player inventory
      const playerEntity = { ...state.entities[player.id] };
      playerEntity.relationships = {
        ...playerEntity.relationships,
        [RelationshipType.CONTAINS]: [
          ...(playerEntity.relationships[RelationshipType.CONTAINS] || []),
          targetEntity.id
        ]
      };

      // Update state
      return {
        ...state,
        entities: {
          ...state.entities,
          [currentLocation.id]: container,
          [player.id]: playerEntity
        }
      };
    });

    // Create take event
    const takeEvent = createEvent(
      StandardEventTypes.ITEM_TAKEN,
      {
        itemName: targetEntity.attributes.name || command.directObject,
        itemId: targetEntity.id
      },
      {
        actor: player.id,
        target: targetEntity.id,
        location: currentLocation.id,
        tags: [StandardEventTags.VISIBLE],
        narrate: true
      }
    );

    // Return success with event and updated context
    return {
      success: true,
      events: [takeEvent],
      metadata: { updatedContext }
    };
  }

  /**
   * Take all visible and takeable objects in the current location
   */
  private async takeAll(context: GameContext): Promise<CommandResult> {
    const { player, currentLocation } = context;

    // Get all entities in the current location
    const containedIds = currentLocation.relationships[RelationshipType.CONTAINS] || [];
    const takeableEntities = containedIds
      .map(id => context.getEntity(id))
      .filter((e): e is Entity => 
        !!e && 
        e.attributes.takeable === true && 
        context.isAccessible(e.id) &&
        e.id !== player.id
      );

    if (takeableEntities.length === 0) {
      return this.createFailureResult('nothingToTake');
    }

    // Take each entity
    let currentContext = context;
    const events = [];

    for (const entity of takeableEntities) {
      // Create a fake command for each entity
      const fakeCommand: ParsedCommand = {
        verb: 'take',
        directObject: entity.attributes.name as string,
        raw: `take ${entity.attributes.name}`,
        originalText: `take ${entity.attributes.name}`,
        qualifiers: {},
        prepositions: {}
      } as ParsedCommand;

      // Execute the command in the updated context
      const result = await Promise.resolve(this.execute(fakeCommand, currentContext));

      if (result.success) {
        // Update context for next iteration
        if (result.metadata?.updatedContext) {
          currentContext = result.metadata.updatedContext as GameContext;
        }
        events.push(...result.events);
      }
    }

    if (events.length === 0) {
      return this.createFailureResult('couldntTakeAnything');
    }

    // Add a summary event
    const summaryEvent = createEvent(
      StandardEventTypes.ITEM_TAKEN,
      {
        multipleItems: true,
        count: events.length,
        itemNames: events.map(e => e.payload?.itemName).filter(Boolean)
      },
      {
        actor: player.id,
        location: currentLocation.id,
        tags: [StandardEventTags.VISIBLE],
        narrate: true
      }
    );

    // Return success with all events and updated context
    return {
      success: true,
      events: [summaryEvent, ...events],
      metadata: { updatedContext: currentContext }
    };
  }

  /**
   * Validate take command
   */
  public validate(command: ParsedCommand, context: GameContext): { valid: boolean; error?: string } {
    if (!command.directObject) {
      return {
        valid: false,
        error: 'missingDirectObject'
      };
    }

    return { valid: true };
  }
}

/**
 * Create a new take handler
 */
export function createTakeHandler(): TakeHandler {
  return new TakeHandler();
}

