}

// packages/lang-en-us/src/english-provider.ts

import { LanguageProvider, VerbDefinition, LanguageParserProvider } from '@sharpee/core';
import { StandardResponses } from './response-templates';
import { StandardVerbDefinitions } from './verb-definitions';
import { createEnglishParserProvider } from './parser';

/**
 * English language implementation
 */
export class EnglishLanguageProvider implements LanguageProvider {
  protected templates: Map<string, string>;
  protected verbs: Map<string, VerbDefinition>;
  protected verbSynonyms: Map<string, string>; // maps synonym -> canonical
  protected parserProvider: LanguageParserProvider;

  constructor() {
    this.templates = new Map();
    this.verbs = new Map();
    this.verbSynonyms = new Map();
    this.parserProvider = createEnglishParserProvider();

    this.initialize();
  }

  /**
   * Initialize with standard values
   */
  protected initialize(): void {
    // Initialize templates
    for (const [key, value] of Object.entries(StandardResponses)) {
      this.templates.set(key, value);
    }

    // Initialize verbs and synonyms
    this.initializeVerbs();
  }

  /**
   * Set up standard verb definitions
   */
  protected initializeVerbs(): void {
    // Add verb definitions from the standard set
    for (const [canonical, def] of Object.entries(StandardVerbDefinitions)) {
      this.verbs.set(canonical, def);

      // Add synonyms to the synonym map
      for (const synonym of def.synonyms) {
        this.verbSynonyms.set(synonym, canonical);
      }
    }
  }

  /**
   * Get all verb definitions
   */
  getVerbs(): Map<string, VerbDefinition> {
    return new Map(this.verbs);
  }

  /**
   * Get synonyms for a canonical verb
   */
  getVerbSynonyms(canonicalVerb: string): string[] {
    const verbDef = this.verbs.get(canonicalVerb);
    return verbDef ? [...verbDef.synonyms] : [];
  }

  /**
   * Get a template string
   */
  getTemplate(key: string): string {
    return this.templates.get(key) || `{MISSING TEMPLATE: ${key}}`;
  }

  /**
   * Format a message using a template and parameters
   */
  formatMessage(templateKey: string, ...params: any[]): string {
    const template = this.getTemplate(templateKey);

    return template.replace(/\{(\d+)\}/g, (match, index) => {
      const paramIndex = parseInt(index, 10);
      return params[paramIndex] !== undefined ? String(params[paramIndex]) : match;
    });
  }

  /**
   * Format a list of items
   */
  formatList(headerTemplate: string, itemTemplate: string, headerParams: any[] = [], items: any[]): string {
    if (items.length === 0) {
      return '';
    }

    const header = this.formatMessage(headerTemplate, ...headerParams);
    const formattedItems = items.map(item => {
      if (typeof item === 'string') {
        return this.formatMessage(itemTemplate, item);
      } else if (item && typeof item === 'object' && 'name' in item) {
        return this.formatMessage(itemTemplate, item.name);
      }
      return this.formatMessage(itemTemplate, String(item));
    });

    return `${header}\n${formattedItems.join('\n')}`;
  }

  /**
   * Check if a word is a known verb
   */
  isVerb(word: string): boolean {
    return this.verbs.has(word) || this.verbSynonyms.has(word);
  }

  /**
   * Get the canonical form of a verb
   */
  getCanonicalVerb(verb: string): string | undefined {
    // If it's already a canonical verb
    if (this.verbs.has(verb)) {
      return verb;
    }

    // Check if it's a synonym
    return this.verbSynonyms.get(verb);
  }

  /**
   * Add custom verbs
   */
  addVerbs(verbs: Record<string, VerbDefinition>): void {
    for (const [canonical, def] of Object.entries(verbs)) {
      this.verbs.set(canonical, def);

      // Add synonyms to the synonym map
      for (const synonym of def.synonyms) {
        this.verbSynonyms.set(synonym, canonical);
      }
    }
  }

  /**
   * Set a template
   */
  setTemplate(key: string, template: string): void {
    this.templates.set(key, template);
  }

  /**
   * Get the parser provider for this language
   */
  getParserProvider(): LanguageParserProvider {
    return this.parserProvider;
  }
}

/**
 * Factory for creating English language providers
 */
export class EnglishLanguageProviderFactory {
  createProvider(options?: any): LanguageProvider {
    return new EnglishLanguageProvider();
  }
}

/**
 * Create a new English language provider
 */
export function createEnglishLanguageProvider(): LanguageProvider {
  return new EnglishLanguageProvider();
}

