/**
 * IF-specific parser interfaces
 */

import { Entity, EntityId } from '../world-model/types';

/**
 * Represents a scored match for an entity
 */
export interface ScoredMatch {
  entity: Entity;
  score: number;
  matchedWords: string[];
  matchType: MatchType;
  disambiguation?: string; // e.g., "the brass one"
}

export enum MatchType {
  EXACT = 'exact',
  PARTIAL = 'partial',
  SYNONYM = 'synonym',
  PRONOUN = 'pronoun',
  IMPLIED = 'implied'
}

/**
 * Scope context for command parsing
 */
export interface ScopeContext {
  visible: Set<EntityId>;
  reachable: Set<EntityId>;
  known: Set<EntityId>;
  special?: Set<EntityId>; // Command-specific scope
  recentlyMentioned: EntityId[];
}

/**
 * Grammar pattern definition
 */
export interface GrammarPattern {
  id: string;
  pattern: string;              // e.g., "take|get|grab <noun>"
  action: string;               // e.g., "taking"
  prepositions?: string[];      // e.g., ["from", "off of"]
  reversed?: boolean;           // e.g., "take <noun> from <second>"
  matchAll?: boolean;           // e.g., "take all"
  requiresSecond?: boolean;     // Must have a second noun
  allowsImplicitSecond?: boolean; // e.g., "unlock door" implies "with key"
}

/**
 * A parsed IF command
 */
export interface IFCommand {
  action: string;                    // Canonical action name
  noun?: ScoredMatch[];              // Possible matches for first noun
  second?: ScoredMatch[];            // Possible matches for second noun
  actor: EntityId;                   // Who's performing (usually player)
  preposition?: string;              // Used preposition
  text?: string;                     // Free text (for "say" commands)
  pattern: GrammarPattern;           // Which pattern matched
  originalInput: string;
  confidence: number;                // How confident in the parse
}

/**
 * Parser result that may need disambiguation
 */
export interface ParseResult {
  success: boolean;
  commands: IFCommand[];
  needsDisambiguation?: DisambiguationRequest;
  error?: string;
}

/**
 * Request for disambiguation from player
 */
export interface DisambiguationRequest {
  prompt: string;
  options: Array<{
    entity: Entity;
    description: string;
  }>;
  context: 'noun' | 'second';
  originalCommand: IFCommand;
}

/**
 * Interface for IF-specific parsers
 */
export interface IFParser {
  /**
   * Parse input into IF commands
   */
  parse(
    input: string,
    scope: ScopeContext,
    getEntity: (id: EntityId) => Entity | undefined
  ): ParseResult;

  /**
   * Continue parsing after disambiguation
   */
  continueWithDisambiguation(
    original: IFCommand,
    choice: EntityId,
    context: 'noun' | 'second'
  ): IFCommand;

  /**
   * Add custom grammar patterns
   */
  addGrammar(pattern: GrammarPattern): void;

  /**
   * Get all registered patterns
   */
  getGrammarPatterns(): GrammarPattern[];
}

/**
 * Match scoring configuration
 */
export interface ScoringConfig {
  exactMatch: number;
  partialMatch: number;
  synonymMatch: number;
  adjectiveMatch: number;
  visibleBonus: number;
  reachableBonus: number;
  recentlyMentionedBonus: number;
  pronounPenalty: number;
}

/**
 * Language-specific parser configuration
 */
export interface IFParserConfig {
  articles: string[];              // ["a", "an", "the"]
  conjunctions: string[];          // ["and", "then", "but"]
  pronouns: string[];              // ["it", "them", "him", "her"]
  implicitPrepositions: Map<string, string>; // "unlock door"  "with"
  directions: string[];            // ["north", "south", "up", "down"]
  scoring: ScoringConfig;
}

/**
 * IF-specific parser implementation
 */

import {
  IFParser,
  IFCommand,
  ParseResult,
  ScopeContext,
  GrammarPattern,
  ScoredMatch,
  MatchType,
  DisambiguationRequest,
  IFParserConfig,
  ScoringConfig
} from './if-parser-types';
import { Entity, EntityId } from '../world-model/types';

/**
 * Default scoring configuration
 */
const DEFAULT_SCORING: ScoringConfig = {
  exactMatch: 100,
  partialMatch: 50,
  synonymMatch: 75,
  adjectiveMatch: 25,
  visibleBonus: 20,
  reachableBonus: 30,
  recentlyMentionedBonus: 40,
  pronounPenalty: -20
};

/**
 * Token types for IF parsing
 */
interface Token {
  text: string;
  type: 'word' | 'punctuation' | 'number';
  position: number;
}

/**
 * Main IF parser implementation
 */
export class IFParserImpl implements IFParser {
  private patterns: Map<string, GrammarPattern> = new Map();
  private config: IFParserConfig;
  private patternCache: Map<string, RegExp> = new Map();

  constructor(config: IFParserConfig) {
    this.config = {
      ...config,
      scoring: { ...DEFAULT_SCORING, ...config.scoring }
    };
    this.initializeStandardPatterns();
  }

  /**
   * Initialize standard IF grammar patterns
   */
  private initializeStandardPatterns(): void {
    // Basic object manipulation
    this.addGrammar({
      id: 'taking',
      pattern: 'take|get|pick up|grab <noun>',
      action: 'taking'
    });

    this.addGrammar({
      id: 'dropping', 
      pattern: 'drop|put down|discard <noun>',
      action: 'dropping'
    });

    this.addGrammar({
      id: 'examining',
      pattern: 'examine|x|look at|l at|inspect <noun>',
      action: 'examining'
    });

    // Two-object commands
    this.addGrammar({
      id: 'inserting',
      pattern: 'put|place|insert <noun> in|into|inside <second>',
      action: 'inserting',
      requiresSecond: true,
      prepositions: ['in', 'into', 'inside']
    });

    this.addGrammar({
      id: 'giving',
      pattern: 'give|offer|hand <noun> to <second>',
      action: 'giving',
      requiresSecond: true,
      prepositions: ['to']
    });

    // Movement
    this.addGrammar({
      id: 'going',
      pattern: 'go|walk|run|move <direction>',
      action: 'going'
    });

    // Special patterns
    this.addGrammar({
      id: 'taking-all',
      pattern: 'take|get all|everything',
      action: 'taking',
      matchAll: true
    });

    this.addGrammar({
      id: 'inventory',
      pattern: 'inventory|inv|i',
      action: 'inventory'
    });
  }

  /**
   * Add a grammar pattern
   */
  addGrammar(pattern: GrammarPattern): void {
    this.patterns.set(pattern.id, pattern);
    // Clear pattern cache as patterns have changed
    this.patternCache.clear();
  }

  /**
   * Get all grammar patterns
   */
  getGrammarPatterns(): GrammarPattern[] {
    return Array.from(this.patterns.values());
  }

  /**
   * Parse input into IF commands
   */
  parse(
    input: string,
    scope: ScopeContext,
    getEntity: (id: EntityId) => Entity | undefined
  ): ParseResult {
    // Tokenize input
    const tokens = this.tokenize(input.toLowerCase().trim());

    // Try to match against grammar patterns
    const matchedPattern = this.findMatchingPattern(tokens);

    if (!matchedPattern) {
      return {
        success: false,
        commands: [],
        error: "I don't understand that command."
      };
    }

    // Extract noun phrases from the input
    const { pattern, nounPhrase, secondPhrase, preposition } = matchedPattern;

    // Build the command
    const command: IFCommand = {
      action: pattern.action,
      actor: 'player', // TODO: Get from context
      pattern: pattern,
      originalInput: input,
      confidence: 1.0,
      preposition
    };

    // Score matches for the first noun
    if (nounPhrase && !pattern.matchAll) {
      const nounMatches = this.findMatches(nounPhrase, scope, getEntity);

      if (nounMatches.length === 0) {
        return {
          success: false,
          commands: [],
          error: `I don't see any "${nounPhrase}" here.`
        };
      }

      command.noun = nounMatches;

      // Check if we need disambiguation for the first noun
      if (nounMatches.length > 1 && this.needsDisambiguation(nounMatches)) {
        return {
          success: true,
          commands: [command],
          needsDisambiguation: this.createDisambiguationRequest(
            nounMatches,
            'noun',
            command
          )
        };
      }
    }

    // Score matches for the second noun
    if (secondPhrase && pattern.requiresSecond) {
      const secondMatches = this.findMatches(secondPhrase, scope, getEntity);

      if (secondMatches.length === 0) {
        return {
          success: false,
          commands: [],
          error: `I don't see any "${secondPhrase}" here.`
        };
      }

      command.second = secondMatches;

      // Check if we need disambiguation for the second noun
      if (secondMatches.length > 1 && this.needsDisambiguation(secondMatches)) {
        return {
          success: true,
          commands: [command],
          needsDisambiguation: this.createDisambiguationRequest(
            secondMatches,
            'second',
            command
          )
        };
      }
    }

    return {
      success: true,
      commands: [command]
    };
  }

  /**
   * Continue parsing after disambiguation
   */
  continueWithDisambiguation(
    original: IFCommand,
    choice: EntityId,
    context: 'noun' | 'second'
  ): IFCommand {
    const updated = { ...original };

    if (context === 'noun' && updated.noun) {
      // Filter to just the chosen entity
      updated.noun = updated.noun.filter(m => m.entity.id === choice);
    } else if (context === 'second' && updated.second) {
      updated.second = updated.second.filter(m => m.entity.id === choice);
    }

    return updated;
  }

  /**
   * Tokenize input
   */
  private tokenize(input: string): Token[] {
    const tokens: Token[] = [];
    const words = input.split(/\s+/);
    let position = 0;

    for (const word of words) {
      // Remove punctuation from end
      const cleanWord = word.replace(/[.,!?;:]$/, '');
      if (cleanWord) {
        tokens.push({
          text: cleanWord,
          type: /^\d+$/.test(cleanWord) ? 'number' : 'word',
          position
        });
      }
      position += word.length + 1;
    }

    return tokens;
  }

  /**
   * Find matching grammar pattern
   */
  private findMatchingPattern(tokens: Token[]): {
    pattern: GrammarPattern;
    nounPhrase?: string;
    secondPhrase?: string;
    preposition?: string;
  } | null {
    const input = tokens.map(t => t.text).join(' ');

    for (const pattern of this.patterns.values()) {
      const regex = this.getPatternRegex(pattern);
      const match = input.match(regex);

      if (match) {
        return {
          pattern,
          nounPhrase: match.groups?.noun,
          secondPhrase: match.groups?.second,
          preposition: match.groups?.prep
        };
      }
    }

    return null;
  }

  /**
   * Convert pattern string to regex
   */
  private getPatternRegex(pattern: GrammarPattern): RegExp {
    const cached = this.patternCache.get(pattern.id);
    if (cached) return cached;

    let regexStr = pattern.pattern;

    // Replace verb alternatives
    regexStr = regexStr.replace(/(\w+)(\|[\w\s]+)+/g, (match) => {
      const alternatives = match.split('|').map(s => s.trim());
      return `(${alternatives.join('|')})`;
    });

    // Replace placeholders
    regexStr = regexStr.replace(/<noun>/g, '(?<noun>.+?)');
    regexStr = regexStr.replace(/<second>/g, '(?<second>.+?)');
    regexStr = regexStr.replace(/<direction>/g, `(?<noun>${this.config.directions.join('|')})`);
    regexStr = regexStr.replace(/<text>/g, '(?<text>.+)');

    // Handle prepositions
    if (pattern.prepositions) {
      const prepPattern = `(?<prep>${pattern.prepositions.join('|')})`;
      regexStr = regexStr.replace(/\bin\|into\|inside\b/g, prepPattern);
      regexStr = regexStr.replace(/\bto\b/g, prepPattern);
    }

    // Anchor to start and end
    regexStr = `^${regexStr}$`;

    const regex = new RegExp(regexStr);
    this.patternCache.set(pattern.id, regex);
    return regex;
  }

  /**
   * Find matching entities
   */
  private findMatches(
    phrase: string,
    scope: ScopeContext,
    getEntity: (id: EntityId) => Entity | undefined
  ): ScoredMatch[] {
    const words = phrase.split(/\s+/);
    const matches: ScoredMatch[] = [];

    // Check pronouns first
    if (words.length === 1 && this.config.pronouns.includes(words[0])) {
      // Handle pronoun resolution
      if (scope.recentlyMentioned.length > 0) {
        const entity = getEntity(scope.recentlyMentioned[0]);
        if (entity) {
          matches.push({
            entity,
            score: 80,
            matchedWords: words,
            matchType: MatchType.PRONOUN
          });
        }
      }
      return matches;
    }

    // Score all entities in scope
    const allInScope = new Set([
      ...scope.visible,
      ...scope.reachable,
      ...scope.known
    ]);

    for (const entityId of allInScope) {
      const entity = getEntity(entityId);
      if (!entity) continue;

      const score = this.scoreEntity(entity, words, scope);
      if (score > 0) {
        matches.push({
          entity,
          score,
          matchedWords: words,
          matchType: this.getMatchType(entity, words)
        });
      }
    }

    // Sort by score descending
    matches.sort((a, b) => b.score - a.score);

    return matches;
  }

  /**
   * Score how well an entity matches the words
   */
  private scoreEntity(
    entity: Entity,
    words: string[],
    scope: ScopeContext
  ): number {
    let score = 0;
    const name = (entity.attributes.name as string || '').toLowerCase();
    const adjectives = (entity.attributes.adjectives as string[] || [])
      .map(a => a.toLowerCase());

    // Filter out articles
    const meaningfulWords = words.filter(w => !this.config.articles.includes(w));

    // Check name match
    for (const word of meaningfulWords) {
      if (name === word) {
        score += this.config.scoring.exactMatch;
      } else if (name.includes(word)) {
        score += this.config.scoring.partialMatch;
      }
    }

    // Check adjective matches
    for (const word of meaningfulWords) {
      if (adjectives.includes(word)) {
        score += this.config.scoring.adjectiveMatch;
      }
    }

    // Apply scope bonuses
    if (scope.visible.has(entity.id)) {
      score += this.config.scoring.visibleBonus;
    }
    if (scope.reachable.has(entity.id)) {
      score += this.config.scoring.reachableBonus;
    }
    if (scope.recentlyMentioned.includes(entity.id)) {
      const recencyIndex = scope.recentlyMentioned.indexOf(entity.id);
      score += this.config.scoring.recentlyMentionedBonus / (recencyIndex + 1);
    }

    return score;
  }

  /**
   * Determine match type
   */
  private getMatchType(entity: Entity, words: string[]): MatchType {
    const name = (entity.attributes.name as string || '').toLowerCase();

    if (words.some(w => name === w)) {
      return MatchType.EXACT;
    }

    if (words.some(w => name.includes(w))) {
      return MatchType.PARTIAL;
    }

    return MatchType.SYNONYM;
  }

  /**
   * Check if disambiguation is needed
   */
  private needsDisambiguation(matches: ScoredMatch[]): boolean {
    if (matches.length <= 1) return false;

    // If top match is significantly better, no disambiguation needed
    const topScore = matches[0].score;
    const secondScore = matches[1].score;

    return (topScore - secondScore) < 30;
  }

  /**
   * Create disambiguation request
   */
  private createDisambiguationRequest(
    matches: ScoredMatch[],
    context: 'noun' | 'second',
    command: IFCommand
  ): DisambiguationRequest {
    const options = matches.slice(0, 5).map(match => ({
      entity: match.entity,
      description: this.getEntityDescription(match.entity)
    }));

    const prompt = context === 'noun' 
      ? "Which do you mean?"
      : "Which do you want to use?";

    return {
      prompt,
      options,
      context,
      originalCommand: command
    };
  }

  /**
   * Get a disambiguation description for an entity
   */
  private getEntityDescription(entity: Entity): string {
    const name = entity.attributes.name as string || 'something';
    const adjectives = entity.attributes.adjectives as string[] || [];

    if (adjectives.length > 0) {
      return `the ${adjectives.join(' ')} ${name}`;
    }

    return `the ${name}`;
  }
}

