/**
 * IF-specific world model
 * 
 * This module provides the simplified, IF-focused world model that replaces
 * the generic graph database approach with specific support for rooms,
 * containers, doors, and other IF concepts.
 */

export * from './if-world';
export * from './scope-calculator';
export * from './location-tracker';
export * from './entity-factory';
export * from './world-queries';

/**
 * IF World Model
 * 
 * A simplified world model specifically designed for interactive fiction.
 * Maintains the graph-based structure but adds IF-specific concepts.
 */

import { Entity, EntityId } from '../types';
import { 
  IFEntity, 
  IFEntityType, 
  Room, 
  Thing, 
  Container, 
  Door,
  Person,
  isRoom,
  isContainer,
  isSupporter,
  isPerson,
  canContain
} from '../if-entities/types';
import { IFRelationship, Direction, directionToRelationship } from '../if-entities/relationships';
import { EventEmitter } from '../implementations/event-emitter';
import { LocationTracker } from './location-tracker';
import { ScopeCalculator } from './scope-calculator';

/**
 * Events emitted by the world model
 */
export interface IFWorldEvents {
  'entity:created': { entity: IFEntity };
  'entity:updated': { entity: IFEntity; previous: IFEntity };
  'entity:deleted': { id: EntityId };
  'entity:moved': { entity: IFEntity; from: EntityId | null; to: EntityId | null };
  'relationship:added': { from: EntityId; to: EntityId; type: IFRelationship };
  'relationship:removed': { from: EntityId; to: EntityId; type: IFRelationship };
}

/**
 * Configuration for the IF world
 */
export interface IFWorldConfig {
  /** ID of the player entity */
  playerId: EntityId;
  /** Starting room ID */
  startingRoom?: EntityId;
  /** Whether to validate capacity constraints */
  enforceCapacity?: boolean;
  /** Whether to track entity history */
  trackHistory?: boolean;
}

/**
 * The main IF world model
 */
export class IFWorld extends EventEmitter<IFWorldEvents> {
  private entities: Map<EntityId, IFEntity> = new Map();
  private locationTracker: LocationTracker;
  private scopeCalculator: ScopeCalculator;
  private config: Required<IFWorldConfig>;

  constructor(config: IFWorldConfig) {
    super();
    this.config = {
      startingRoom: config.startingRoom || 'void',
      enforceCapacity: config.enforceCapacity ?? true,
      trackHistory: config.trackHistory ?? false,
      ...config
    };

    this.locationTracker = new LocationTracker();
    this.scopeCalculator = new ScopeCalculator(this);
  }

  /**
   * Add an entity to the world
   */
  addEntity(entity: IFEntity, location?: EntityId): void {
    // Validate entity
    this.validateEntity(entity);

    // Store entity
    this.entities.set(entity.id, entity);

    // Set initial location
    if (location) {
      this.locationTracker.setLocation(entity.id, location);
    }

    // Emit event
    this.emit('entity:created', { entity });
  }

  /**
   * Update an entity
   */
  updateEntity(id: EntityId, updates: Partial<IFEntity>): void {
    const entity = this.getEntity(id);
    if (!entity) {
      throw new Error(`Entity ${id} not found`);
    }

    const previous = { ...entity };
    const updated = {
      ...entity,
      ...updates,
      id: entity.id, // Prevent ID changes
      type: entity.type // Prevent type changes
    } as IFEntity;

    this.entities.set(id, updated);
    this.emit('entity:updated', { entity: updated, previous });
  }

  /**
   * Delete an entity
   */
  deleteEntity(id: EntityId): void {
    const entity = this.entities.get(id);
    if (!entity) return;

    // Remove from location tracking
    this.locationTracker.removeEntity(id);

    // Remove all relationships
    for (const [otherId, other] of this.entities) {
      if (otherId === id) continue;

      for (const relType in other.relationships) {
        const rels = other.relationships[relType];
        const index = rels.indexOf(id);
        if (index >= 0) {
          rels.splice(index, 1);
        }
      }
    }

    // Delete entity
    this.entities.delete(id);
    this.emit('entity:deleted', { id });
  }

  /**
   * Get an entity by ID
   */
  getEntity(id: EntityId): IFEntity | undefined {
    return this.entities.get(id);
  }

  /**
   * Get all entities
   */
  getAllEntities(): IFEntity[] {
    return Array.from(this.entities.values());
  }

  /**
   * Move an entity to a new location
   */
  moveEntity(entityId: EntityId, toLocation: EntityId | null): void {
    const entity = this.getEntity(entityId);
    if (!entity) {
      throw new Error(`Entity ${entityId} not found`);
    }

    const toEntity = toLocation ? this.getEntity(toLocation) : null;

    // Validate destination
    if (toLocation && toEntity) {
      if (!canContain(toEntity)) {
        throw new Error(`${toLocation} cannot contain other entities`);
      }

      // Check capacity
      if (this.config.enforceCapacity) {
        const capacity = (toEntity.attributes.capacity as number) ?? -1;
        if (capacity >= 0) {
          const contents = this.getContents(toLocation);
          if (contents.length >= capacity) {
            throw new Error(`${toLocation} is full`);
          }
        }
      }
    }

    // Track movement
    const fromLocation = this.locationTracker.getLocation(entityId);
    this.locationTracker.setLocation(entityId, toLocation);

    // Update relationships
    if (fromLocation) {
      this.removeRelationship(fromLocation, entityId, IFRelationship.CONTAINS);
    }
    if (toLocation) {
      this.addRelationship(toLocation, entityId, IFRelationship.CONTAINS);
    }

    // Emit event
    this.emit('entity:moved', { 
      entity, 
      from: fromLocation, 
      to: toLocation 
    });
  }

  /**
   * Get the current location of an entity
   */
  getLocation(entityId: EntityId): EntityId | null {
    return this.locationTracker.getLocation(entityId);
  }

  /**
   * Get all entities at a location
   */
  getContents(locationId: EntityId): IFEntity[] {
    const contents = this.locationTracker.getContents(locationId);
    return contents
      .map(id => this.getEntity(id))
      .filter((e): e is IFEntity => e !== undefined);
  }

  /**
   * Add a relationship between entities
   */
  addRelationship(fromId: EntityId, toId: EntityId, type: IFRelationship): void {
    const from = this.getEntity(fromId);
    const to = this.getEntity(toId);

    if (!from || !to) {
      throw new Error('Both entities must exist');
    }

    // Initialize relationship array if needed
    if (!from.relationships[type]) {
      from.relationships[type] = [];
    }

    // Add if not already present
    if (!from.relationships[type].includes(toId)) {
      from.relationships[type].push(toId);
      this.emit('relationship:added', { from: fromId, to: toId, type });
    }
  }

  /**
   * Remove a relationship between entities
   */
  removeRelationship(fromId: EntityId, toId: EntityId, type: IFRelationship): void {
    const from = this.getEntity(fromId);
    if (!from || !from.relationships[type]) return;

    const index = from.relationships[type].indexOf(toId);
    if (index >= 0) {
      from.relationships[type].splice(index, 1);
      this.emit('relationship:removed', { from: fromId, to: toId, type });
    }
  }

  /**
   * Add an exit from one room to another
   */
  addExit(fromRoom: EntityId, direction: Direction, toRoom: EntityId): void {
    const from = this.getEntity(fromRoom);
    const to = this.getEntity(toRoom);

    if (!from || !to) {
      throw new Error('Both rooms must exist');
    }

    if (!isRoom(from) || !isRoom(to)) {
      throw new Error('Both entities must be rooms');
    }

    const relationship = directionToRelationship(direction);
    this.addRelationship(fromRoom, toRoom, relationship);
  }

  /**
   * Get room in a direction
   */
  getRoomInDirection(fromRoom: EntityId, direction: Direction): EntityId | undefined {
    const room = this.getEntity(fromRoom);
    if (!room || !isRoom(room)) return undefined;

    const relationship = directionToRelationship(direction);
    const targets = room.relationships[relationship] || [];
    return targets[0]; // Assume one room per direction
  }

  /**
   * Get the player entity
   */
  getPlayer(): IFEntity | undefined {
    return this.getEntity(this.config.playerId);
  }

  /**
   * Get the player's current location
   */
  getPlayerLocation(): EntityId | null {
    return this.locationTracker.getLocation(this.config.playerId);
  }

  /**
   * Calculate scope for the current player location
   */
  calculateScope(): ScopeCalculator.ScopeResult {
    return this.scopeCalculator.calculateScope(this.config.playerId);
  }

  /**
   * Check if an entity is visible to the player
   */
  isVisible(entityId: EntityId): boolean {
    const scope = this.calculateScope();
    return scope.visible.has(entityId);
  }

  /**
   * Check if an entity is reachable by the player
   */
  isReachable(entityId: EntityId): boolean {
    const scope = this.calculateScope();
    return scope.reachable.has(entityId);
  }

  /**
   * Validate an entity
   */
  private validateEntity(entity: IFEntity): void {
    if (!entity.id) {
      throw new Error('Entity must have an ID');
    }

    if (!entity.type) {
      throw new Error('Entity must have a type');
    }

    if (!entity.attributes) {
      throw new Error('Entity must have attributes');
    }

    if (!entity.attributes.name) {
      throw new Error('Entity must have a name');
    }

    // Type-specific validation
    switch (entity.type) {
      case IFEntityType.ROOM:
        if (entity.attributes.takeable === true) {
          throw new Error('Rooms cannot be takeable');
        }
        break;

      case IFEntityType.DOOR:
        if (entity.attributes.takeable === true) {
          throw new Error('Doors cannot be takeable');
        }
        if (entity.attributes.openable !== true) {
          throw new Error('Doors must be openable');
        }
        break;

      case IFEntityType.CONTAINER:
        if (entity.attributes.container !== true) {
          throw new Error('Containers must have container=true');
        }
        break;

      case IFEntityType.DEVICE:
        if (entity.attributes.switchable !== true) {
          throw new Error('Devices must be switchable');
        }
        break;
    }
  }
}

import { IFWorld } from '../if-world';
import { IFEntityType, IFEntity, Room, Thing, Container, Door, Person, Device } from '../if-entities/types';
import { IFRelationship, Direction } from '../if-entities/relationships';

// Test utilities
const createRoom = (id: string, name = 'A Room'): Room => ({
  id,
  type: IFEntityType.ROOM,
  attributes: {
    name,
    description: 'A generic room',
    article: 'a',
  },
});

const createThing = (id: string, name = 'thing'): Thing => ({
  id,
  type: IFEntityType.THING,
  attributes: {
    name,
    article: 'a',
  },
});

const createContainer = (id: string, name = 'container'): Container => ({
  id,
  type: IFEntityType.CONTAINER,
  attributes: {
    name,
    container: true,
    article: 'a',
  },
});

describe('IFWorld', () => {
  let world: IFWorld;

  beforeEach(() => {
    world = new IFWorld({ playerId: 'player' });
  });

  describe('entity management', () => {
    it('should add and retrieve entities', () => {
      const room = createRoom('room1');
      world.addEntity(room);
      const retrieved = world.getEntity('room1');
      expect(retrieved).toEqual(room);
    });

    it('should update entity attributes', () => {
      const thing = createThing('thing1');
      world.addEntity(thing);

      const updated = {
        ...thing,
        attributes: { ...thing.attributes, description: 'Updated description' }
      };

      world.updateEntity('thing1', updated);
      expect(world.getEntity('thing1')).toEqual(updated);
    });

    it('should delete entities', () => {
      const thing = createThing('thing1');
      world.addEntity(thing);
      world.deleteEntity('thing1');
      expect(world.getEntity('thing1')).toBeUndefined();
    });
  });

  describe('relationships', () => {
    it('should create and query relationships', () => {
      const room1 = createRoom('room1');
      const room2 = createRoom('room2');
      const door = {
        id: 'door1',
        type: IFEntityType.DOOR,
        attributes: {
          name: 'door',
          openable: true,
          article: 'a',
        },
      };

      world.addEntity(room1);
      world.addEntity(room2);
      world.addEntity(door);

      // Connect rooms with door
      world.addRelationship(door.id, room1.id, IFRelationship.CONNECTS);
      world.addRelationship(door.id, room2.id, IFRelationship.CONNECTS);

      // Check relationships
      expect(world.getRelationships(door.id)).toContainEqual({
        from: door.id,
        to: room1.id,
        type: IFRelationship.CONNECTS,
      });
      expect(world.getRelationships(room1.id)).toContainEqual({
        from: door.id,
        to: room1.id,
        type: IFRelationship.CONNECTS,
      });
    });

    it('should handle inverse relationships', () => {
      const room1 = createRoom('room1');
      const room2 = createRoom('room2');

      world.addEntity(room1);
      world.addEntity(room2);

      // Set up north/south relationship
      world.addRelationship(room1.id, room2.id, IFRelationship.NORTH_OF);

      // Check inverse relationship
      expect(world.getRelationships(room2.id)).toContainEqual({
        from: room1.id,
        to: room2.id,
        type: IFRelationship.NORTH_OF,
      });
      expect(world.getRelationships(room1.id)).toContainEqual({
        from: room2.id,
        to: room1.id,
        type: IFRelationship.SOUTH_OF,
      });
    });
  });

  describe('container mechanics', () => {
    it('should handle containment', () => {
      const container = createContainer('container1');
      const thing = createThing('thing1');

      world.addEntity(container);
      world.addEntity(thing);

      // Put thing in container
      world.moveEntity(thing.id, container.id);

      // Check location
      expect(world.getLocation(thing.id)).toBe(container.id);
      expect(world.getContents(container.id)).toContain(thing.id);
    });

    it('should enforce capacity constraints', () => {
      const container = {
        id: 'container1',
        type: IFEntityType.CONTAINER,
        attributes: {
          name: 'small container',
          container: true,
          capacity: 2,
          article: 'a',
        },
      };

      world.addEntity(container);

      // Add first item
      const thing1 = createThing('thing1');
      world.addEntity(thing1);
      world.moveEntity(thing1.id, container.id);

      // Add second item
      const thing2 = createThing('thing2');
      world.addEntity(thing2);
      world.moveEntity(thing2.id, container.id);

      // Try to add third item - should fail
      const thing3 = createThing('thing3');
      world.addEntity(thing3);
      expect(() => world.moveEntity(thing3.id, container.id)).toThrow();
    });
  });

  describe('scope and visibility', () => {
    it('should calculate scope correctly', () => {
      const room = createRoom('room1');
      const container = createContainer('container1');
      const thing = createThing('thing1');

      world.addEntity(room);
      world.addEntity(container);
      world.addEntity(thing);

      // Put container in room
      world.moveEntity(container.id, room.id);
      // Put thing in container
      world.moveEntity(thing.id, container.id);

      // Check scope from room
      const scope = world.calculateScope(room.id);
      expect(scope).toContain(container.id);
      expect(scope).toContain(thing.id);
    });

    it('should handle visibility and concealment', () => {
      const room = createRoom('room1');
      const thing = {
        id: 'thing1',
        type: IFEntityType.THING,
        attributes: {
          name: 'hidden thing',
          visible: false,
          concealed: true,
          article: 'a',
        },
      };

      world.addEntity(room);
      world.addEntity(thing);
      world.moveEntity(thing.id, room.id);

      // Check scope - hidden thing should not be visible
      const scope = world.calculateScope(room.id);
      expect(scope).not.toContain(thing.id);
    });
  });

  describe('player mechanics', () => {
    it('should track player location', () => {
      const room = createRoom('room1');
      world.addEntity(room);

      // Move player to room
      world.moveEntity('player', room.id);

      expect(world.getPlayerLocation()).toBe(room.id);
    });

    it('should handle player inventory', () => {
      const thing = createThing('thing1');
      world.addEntity(thing);

      // Pick up item
      world.moveEntity(thing.id, 'player');

      expect(world.getPlayerInventory()).toContain(thing.id);
    });
  });
});

