// Export all types
export * from './entity';
export * from './relationship';
export * from './attribute';
export * from './state-tree';
export * from './query';
export * from './extension';

// packages/core/src/world-model/types/entity.ts

/**
 * Unique identifier for entities in the world
 */
export type EntityId = string;

/**
 * The base Entity interface representing any object in the world model
 */
export interface Entity {
  /**
   * Unique identifier for this entity
   */
  id: EntityId;
  
  /**
   * The entity type, used for categorization and type checking
   */
  type: string;
  
  /**
   * Arbitrary attributes/properties of the entity
   */
  attributes: Record<string, unknown>;
  
  /**
   * Relationships to other entities, organized by relationship type
   */
  relationships: Record<string, EntityId[]>;
}

/**
 * Minimal information needed to create a new entity
 */
export interface EntityCreationParams {
  type: string;
  attributes?: Record<string, unknown>;
  relationships?: Record<string, EntityId[]>;
}

/**
 * Configuration for how entity operations should be performed
 */
export interface EntityOperationOptions {
  /**
   * Whether to merge arrays in relationships instead of replacing them
   */
  mergeRelationships?: boolean;
  
  /**
   * Whether to validate relationship target existence
   */
  validateRelationships?: boolean;
}

/**
 * Factory for creating IF entities with proper defaults
 */

import { EntityId } from '../types';
import {
  IFEntity,
  IFEntityType,
  Room,
  Thing,
  Container,
  Supporter,
  Door,
  Person,
  Device,
  RoomAttributes,
  ThingAttributes,
  ContainerAttributes,
  SupporterAttributes,
  DoorAttributes,
  PersonAttributes,
  DeviceAttributes
} from '../if-entities/types';

/**
 * Base creation parameters for all entities
 */
interface BaseEntityParams {
  id: EntityId;
  name: string;
  description?: string;
  adjectives?: string[];
  synonyms?: string[];
  visible?: boolean;
  concealed?: boolean;
  scenery?: boolean;
}

/**
 * Room creation parameters
 */
export interface RoomParams extends BaseEntityParams {
  dark?: boolean;
  indoors?: boolean;
  visited?: boolean;
}

/**
 * Thing creation parameters
 */
export interface ThingParams extends BaseEntityParams {
  takeable?: boolean;
  fixed?: boolean;
  edible?: boolean;
  wearable?: boolean;
  bulk?: number;
  readable?: boolean;
  text?: string;
}

/**
 * Container creation parameters
 */
export interface ContainerParams extends ThingParams {
  capacity?: number;
  transparent?: boolean;
  openable?: boolean;
  open?: boolean;
  lockable?: boolean;
  locked?: boolean;
  key?: EntityId;
}

/**
 * Supporter creation parameters
 */
export interface SupporterParams extends ThingParams {
  capacity?: number;
}

/**
 * Door creation parameters
 */
export interface DoorParams extends BaseEntityParams {
  open?: boolean;
  lockable?: boolean;
  locked?: boolean;
  key?: EntityId;
}

/**
 * Person creation parameters
 */
export interface PersonParams extends BaseEntityParams {
  proper?: boolean;
  gender?: 'male' | 'female' | 'neutral' | 'unknown';
}

/**
 * Device creation parameters
 */
export interface DeviceParams extends ThingParams {
  on?: boolean;
  providesLight?: boolean;
}

/**
 * Factory class for creating IF entities
 */
export class EntityFactory {
  /**
   * Create a room
   */
  static createRoom(params: RoomParams): Room {
    const attributes: RoomAttributes = {
      name: params.name,
      description: params.description,
      adjectives: params.adjectives || [],
      synonyms: params.synonyms || [],
      visible: params.visible ?? true,
      concealed: params.concealed ?? false,
      scenery: params.scenery ?? false,
      dark: params.dark ?? false,
      indoors: params.indoors ?? true,
      visited: params.visited ?? false,
      takeable: false,
      fixed: true
    };

    return {
      id: params.id,
      type: IFEntityType.ROOM,
      attributes,
      relationships: {}
    };
  }

  /**
   * Create a thing (basic object)
   */
  static createThing(params: ThingParams): Thing {
    const attributes: ThingAttributes = {
      name: params.name,
      description: params.description,
      adjectives: params.adjectives || [],
      synonyms: params.synonyms || [],
      visible: params.visible ?? true,
      concealed: params.concealed ?? false,
      scenery: params.scenery ?? false,
      takeable: params.takeable ?? true,
      fixed: params.fixed ?? false,
      edible: params.edible ?? false,
      wearable: params.wearable ?? false,
      bulk: params.bulk ?? 1,
      readable: params.readable ?? false,
      text: params.text
    };

    return {
      id: params.id,
      type: IFEntityType.THING,
      attributes,
      relationships: {}
    };
  }

  /**
   * Create a container
   */
  static createContainer(params: ContainerParams): Container {
    const attributes: ContainerAttributes = {
      name: params.name,
      description: params.description,
      adjectives: params.adjectives || [],
      synonyms: params.synonyms || [],
      visible: params.visible ?? true,
      concealed: params.concealed ?? false,
      scenery: params.scenery ?? false,
      takeable: params.takeable ?? true,
      fixed: params.fixed ?? false,
      edible: params.edible ?? false,
      wearable: params.wearable ?? false,
      bulk: params.bulk ?? 2,
      readable: params.readable ?? false,
      text: params.text,
      container: true,
      capacity: params.capacity ?? -1, // -1 = unlimited
      transparent: params.transparent ?? false,
      openable: params.openable ?? true,
      open: params.open ?? false,
      lockable: params.lockable ?? false,
      locked: params.locked ?? false,
      key: params.key
    };

    return {
      id: params.id,
      type: IFEntityType.CONTAINER,
      attributes,
      relationships: {}
    };
  }

  /**
   * Create a supporter
   */
  static createSupporter(params: SupporterParams): Supporter {
    const attributes: SupporterAttributes = {
      name: params.name,
      description: params.description,
      adjectives: params.adjectives || [],
      synonyms: params.synonyms || [],
      visible: params.visible ?? true,
      concealed: params.concealed ?? false,
      scenery: params.scenery ?? false,
      takeable: params.takeable ?? false, // Supporters are usually furniture
      fixed: params.fixed ?? true,
      edible: params.edible ?? false,
      wearable: params.wearable ?? false,
      bulk: params.bulk ?? 5,
      readable: params.readable ?? false,
      text: params.text,
      supporter: true,
      capacity: params.capacity ?? -1 // -1 = unlimited
    };

    return {
      id: params.id,
      type: IFEntityType.SUPPORTER,
      attributes,
      relationships: {}
    };
  }

