/**
 * IF-specific relationship types
 */

/**
 * Standard IF relationships between entities
 */
export enum IFRelationship {
  // Containment
  CONTAINS = 'contains',      // Container/Room/Person -> Things
  SUPPORTS = 'supports',      // Supporter -> Things  
  WORN_BY = 'worn_by',       // Thing -> Person
  CARRIED_BY = 'carried_by', // Thing -> Person

  // Spatial
  NORTH_OF = 'north_of',
  SOUTH_OF = 'south_of',
  EAST_OF = 'east_of',
  WEST_OF = 'west_of',
  NORTHEAST_OF = 'northeast_of',
  NORTHWEST_OF = 'northwest_of',
  SOUTHEAST_OF = 'southeast_of',
  SOUTHWEST_OF = 'southwest_of',
  UP_FROM = 'up_from',
  DOWN_FROM = 'down_from',
  IN_FROM = 'in_from',
  OUT_FROM = 'out_from',

  // Connections
  CONNECTS = 'connects',      // Door -> Rooms
  LEADS_TO = 'leads_to',     // Room -> Room (via exit)

  // Unlocking
  UNLOCKS = 'unlocks',       // Key -> Door/Container
  LOCKED_BY = 'locked_by',   // Door/Container -> Key

  // Component/Part relationships
  PART_OF = 'part_of',       // Thing -> Thing
  HAS_COMPONENT = 'has_component', // Thing -> Thing

  // Knowledge/Discovery
  REVEALS = 'reveals',       // Thing/Action -> Thing
  REQUIRES = 'requires',     // Thing/Action -> Thing

  // Other
  BLOCKS = 'blocks',         // Thing -> Exit/Action
  RELATED_TO = 'related_to'  // General relationship
}

/**
 * Helper to get the inverse of a relationship
 */
export function getInverseRelationship(rel: IFRelationship): IFRelationship | null {
  switch (rel) {
    case IFRelationship.CONTAINS:
      return null; // Use location tracking instead
    case IFRelationship.SUPPORTS:
      return null; // Use location tracking instead
    case IFRelationship.WORN_BY:
      return null; // Track on the item
    case IFRelationship.CARRIED_BY:
      return null; // Track on the item

    case IFRelationship.NORTH_OF:
      return IFRelationship.SOUTH_OF;
    case IFRelationship.SOUTH_OF:
      return IFRelationship.NORTH_OF;
    case IFRelationship.EAST_OF:
      return IFRelationship.WEST_OF;
    case IFRelationship.WEST_OF:
      return IFRelationship.EAST_OF;
    case IFRelationship.NORTHEAST_OF:
      return IFRelationship.SOUTHWEST_OF;
    case IFRelationship.NORTHWEST_OF:
      return IFRelationship.SOUTHEAST_OF;
    case IFRelationship.SOUTHEAST_OF:
      return IFRelationship.NORTHWEST_OF;
    case IFRelationship.SOUTHWEST_OF:
      return IFRelationship.NORTHEAST_OF;
    case IFRelationship.UP_FROM:
      return IFRelationship.DOWN_FROM;
    case IFRelationship.DOWN_FROM:
      return IFRelationship.UP_FROM;
    case IFRelationship.IN_FROM:
      return IFRelationship.OUT_FROM;
    case IFRelationship.OUT_FROM:
      return IFRelationship.IN_FROM;

    case IFRelationship.UNLOCKS:
      return IFRelationship.LOCKED_BY;
    case IFRelationship.LOCKED_BY:
      return IFRelationship.UNLOCKS;

    case IFRelationship.PART_OF:
      return IFRelationship.HAS_COMPONENT;
    case IFRelationship.HAS_COMPONENT:
      return IFRelationship.PART_OF;

    default:
      return null;
  }
}

/**
 * Exit/direction names
 */
export type Direction = 
  | 'north' | 'south' | 'east' | 'west'
  | 'northeast' | 'northwest' | 'southeast' | 'southwest'
  | 'up' | 'down' | 'in' | 'out';

/**
 * Map directions to relationships
 */
export function directionToRelationship(direction: Direction): IFRelationship {
  switch (direction) {
    case 'north': return IFRelationship.NORTH_OF;
    case 'south': return IFRelationship.SOUTH_OF;
    case 'east': return IFRelationship.EAST_OF;
    case 'west': return IFRelationship.WEST_OF;
    case 'northeast': return IFRelationship.NORTHEAST_OF;
    case 'northwest': return IFRelationship.NORTHWEST_OF;
    case 'southeast': return IFRelationship.SOUTHEAST_OF;
    case 'southwest': return IFRelationship.SOUTHWEST_OF;
    case 'up': return IFRelationship.UP_FROM;
    case 'down': return IFRelationship.DOWN_FROM;
    case 'in': return IFRelationship.IN_FROM;
    case 'out': return IFRelationship.OUT_FROM;
  }
}

/**
 * Get opposite direction
 */
export function getOppositeDirection(direction: Direction): Direction {
  switch (direction) {
    case 'north': return 'south';
    case 'south': return 'north';
    case 'east': return 'west';
    case 'west': return 'east';
    case 'northeast': return 'southwest';
    case 'northwest': return 'southeast';
    case 'southeast': return 'northwest';
    case 'southwest': return 'northeast';
    case 'up': return 'down';
    case 'down': return 'up';
    case 'in': return 'out';
    case 'out': return 'in';
  }
}

