        ['shelves', 'shelf'],
        ['thieves', 'thief'],
        ['wolves', 'wolf']
      ]),

      abbreviations: new Map([
        ['n', 'north'],
        ['s', 'south'],
        ['e', 'east'],
        ['w', 'west'],
        ['u', 'up'],
        ['d', 'down'],
        ['ne', 'northeast'],
