// packages/core/tests/language/language-provider.test.ts

import { 
  LanguageProvider,
  getLanguageRegistry,
  createDefaultLanguageProvider,
  DefaultLanguageProviderFactory,
  StandardTemplateKey,
  VerbDefinition
} from '../../src/language';

describe('Language Provider System', () => {
  // Reset registry before each test
  beforeEach(() => {
    // Clear the registry by re-registering only the default provider
    const registry = getLanguageRegistry();

    // Remove all existing providers
    registry.getRegisteredLanguages().forEach(lang => {
      // We can't actually remove providers, but we can overwrite them
      registry.registerLanguage(lang, new DefaultLanguageProviderFactory());
    });

    // Register default again to ensure clean state
    registry.registerLanguage('default', new DefaultLanguageProviderFactory());
    registry.setLanguage('default');
  });

  describe('DefaultLanguageProvider', () => {
    let provider: LanguageProvider;

    beforeEach(() => {
      provider = createDefaultLanguageProvider();
    });

    test('should provide basic templates', () => {
      expect(provider.getTemplate(StandardTemplateKey.TAKE_SUCCESS)).toBe('You take {0}.');
      expect(provider.getTemplate(StandardTemplateKey.DROP_SUCCESS)).toBe('You drop {0}.');
    });

    test('should format messages with parameters', () => {
      const formatted = provider.formatMessage(StandardTemplateKey.TAKE_SUCCESS, 'the book');
      expect(formatted).toBe('You take the book.');
    });

    test('should handle missing templates', () => {
      const formatted = provider.formatMessage('NONEXISTENT_TEMPLATE', 'test');
      expect(formatted).toContain('MISSING TEMPLATE');
    });

    test('should allow adding custom templates', () => {
      provider.setTemplate('CUSTOM_TEMPLATE', 'This is a custom template with {0}.');
      const formatted = provider.formatMessage('CUSTOM_TEMPLATE', 'parameters');
      expect(formatted).toBe('This is a custom template with parameters.');
    });

    test('should have basic verbs defined', () => {
      const verbs = provider.getVerbs();
      expect(verbs.size).toBeGreaterThan(0);
      expect(verbs.has('look')).toBeTruthy();
      expect(verbs.has('take')).toBeTruthy();
    });

    test('should identify verbs correctly', () => {
      expect(provider.isVerb('look')).toBeTruthy();
      expect(provider.isVerb('examine')).toBeTruthy(); // Synonym
      expect(provider.isVerb('nonexistentverb')).toBeFalsy();
    });

    test('should get canonical verbs', () => {
      expect(provider.getCanonicalVerb('look')).toBe('look');
      expect(provider.getCanonicalVerb('examine')).toBe('look'); // Maps synonym to canonical
      expect(provider.getCanonicalVerb('nonexistentverb')).toBeUndefined();
    });

    test('should allow adding custom verbs', () => {
      const customVerbs: Record<string, VerbDefinition> = {
        'teleport': {
          canonical: 'teleport',
          synonyms: ['warp', 'beam'],
          description: 'Magically transport to a location',
          category: 'magic',
          requiresDirectObject: true
        }
      };

      provider.addVerbs(customVerbs);

      // Check the verb was added
      expect(provider.isVerb('teleport')).toBeTruthy();
      expect(provider.isVerb('warp')).toBeTruthy();

      // Check synonyms map to the canonical verb
      expect(provider.getCanonicalVerb('warp')).toBe('teleport');

      // Check the verb definition
      const verbs = provider.getVerbs();
      const teleport = verbs.get('teleport');
      expect(teleport).toBeDefined();
      expect(teleport?.category).toBe('magic');
    });

    test('should format lists correctly', () => {
      const items = ['apple', 'banana', 'orange'];
      const formatted = provider.formatList(
        StandardTemplateKey.INVENTORY_HEADER,
        StandardTemplateKey.INVENTORY_ITEM,
        [],
        items
      );

      expect(formatted).toContain('You are carrying:');
      expect(formatted).toContain('- apple');
      expect(formatted).toContain('- banana');
      expect(formatted).toContain('- orange');
    });
  });

  describe('LanguageRegistry', () => {
    let registry = getLanguageRegistry();

    test('should register language providers', () => {
      const factory = new DefaultLanguageProviderFactory();
      registry.registerLanguage('test-lang', factory);

      expect(registry.hasLanguage('test-lang')).toBeTruthy();
      expect(registry.getRegisteredLanguages()).toContain('test-lang');
    });

    test('should activate a registered language', () => {
      const factory = new DefaultLanguageProviderFactory();
      registry.registerLanguage('test-lang', factory);

      const result = registry.setLanguage('test-lang');
      expect(result).toBeTruthy();
      expect(registry.getActiveLanguage()).toBe('test-lang');
    });

    test('should fail to activate an unregistered language', () => {
      const result = registry.setLanguage('nonexistent-lang');
      expect(result).toBeFalsy();
    });

    test('should get the active provider', () => {
      const factory = new DefaultLanguageProviderFactory();
      registry.registerLanguage('test-lang', factory);
      registry.setLanguage('test-lang');

      const provider = registry.getProvider();
      expect(provider).toBeDefined();
      expect(provider?.isVerb('look')).toBeTruthy();
    });

    test('should normalize language codes to lowercase', () => {
      const factory = new DefaultLanguageProviderFactory();
      registry.registerLanguage('Test-Lang-UPPER', factory);

      expect(registry.hasLanguage('test-lang-upper')).toBeTruthy();

      const result = registry.setLanguage('TEST-LANG-upper');
      expect(result).toBeTruthy();
      expect(registry.getActiveLanguage()).toBe('test-lang-upper');
    });
  });
});

/**
 * Simple Rule System v2 Tests
 */

import { 
  createSimpleRuleSystem, 
  createSimpleRuleWorld,
  getTargetItem,
  getAttribute,
  entityIs,
  giveAbility,
  itemTooHeavy,
  isTaking
} from '../../src/rules';
import { createEvent } from '../../src/events/event-system';
import { StandardEventTypes } from '../../src/events/standard-events';

describe('Simple Rule System v2', () => {
  // Simple test world state
  const worldState = {
    entities: {
      'player': {
        id: 'player',
        attributes: { name: 'Player', strength: 10 }
      },
      'apple': {
        id: 'apple',
        attributes: { name: 'apple', weight: 1 }
      },
      'heavy-statue': {
        id: 'heavy-statue',
        attributes: { name: 'heavy statue', weight: 50 }
      },
      'magic-mirror': {
        id: 'magic-mirror',
        attributes: { name: 'magic mirror', weight: 5 }
      }
    }
  };

  let ruleSystem: any;
  let world: any;

  beforeEach(() => {
    // Reset world state
    worldState.entities.player.attributes = { name: 'Player', strength: 10 };

    ruleSystem = createSimpleRuleSystem();
    world = createSimpleRuleWorld(worldState, 'player');
  });

  test('Should prevent taking heavy items', () => {
    // Add a rule that prevents taking heavy items
    ruleSystem.addRule({
      id: 'prevent-heavy-items',
      eventType: 'item:taken',
      condition: itemTooHeavy,
      action: () => ({
        prevent: true,
        message: "That's too heavy for you to lift!"
      })
    });

    // Create event for taking heavy statue
    const event = createEvent(
      StandardEventTypes.ITEM_TAKEN,
      { itemId: 'heavy-statue' },
      { actor: 'player', target: 'heavy-statue' }
    );

    // Process the event
    const result = ruleSystem.processEvent(event, world);

    // Check results
    expect(result.prevent).toBe(true);
    expect(result.events).toHaveLength(1);
    expect(result.events[0].type).toBe('narrative:event');
    expect(result.events[0].payload.message).toBe("That's too heavy for you to lift!");
  });

  test('Should give abilities when taking special items', () => {
    // Add a rule that gives mirror power when taking the magic mirror
    ruleSystem.addRule({
      id: 'magic-mirror-power',
      eventType: 'item:taken',
      condition: isTaking('magic-mirror'),
      action: (event) => ({
        message: 'As you touch the mirror, you feel strange power flowing through you.',
        changes: [giveAbility('player', 'mirror-power')]
      })
    });

    // Create event for taking magic mirror
    const event = createEvent(
      StandardEventTypes.ITEM_TAKEN,
      { itemId: 'magic-mirror' },
      { actor: 'player', target: 'magic-mirror' }
    );

    // Process the event
    const result = ruleSystem.processEvent(event, world);

    // Check results
    expect(result.prevent).toBe(false);
    expect(result.events).toHaveLength(1);
    expect(result.events[0].payload.message).toBe('As you touch the mirror, you feel strange power flowing through you.');

    // Check that ability was added
    const player = world.getPlayer();
    expect(player.attributes['ability_mirror-power']).toBe(true);
  });

  test('Should handle multiple rules in priority order', () => {
    // Add high priority rule
    ruleSystem.addRule({
      id: 'high-priority',
      eventType: 'item:taken',
      priority: 100,
      action: () => ({ message: 'High priority message' })
    });

    // Add low priority rule
    ruleSystem.addRule({
      id: 'low-priority', 
      eventType: 'item:taken',
      priority: 1,
      action: () => ({ message: 'Low priority message' })
    });

    // Create event
    const event = createEvent(
      StandardEventTypes.ITEM_TAKEN,
      { itemId: 'apple' },
      { actor: 'player', target: 'apple' }
    );

    // Process the event
    const result = ruleSystem.processEvent(event, world);

    // Check that high priority rule ran first
    expect(result.events).toHaveLength(2);
    expect(result.events[0].payload.message).toBe('High priority message');
    expect(result.events[1].payload.message).toBe('Low priority message');
  });

  test('Should stop processing when rule prevents action', () => {
    // Add preventing rule
    ruleSystem.addRule({
      id: 'prevent-rule',
      eventType: 'item:taken',
      priority: 100,
      action: () => ({
        prevent: true,
        message: 'Action prevented!'
      })
    });

    // Add rule that should not run
    ruleSystem.addRule({
      id: 'should-not-run',
      eventType: 'item:taken',
      priority: 50,
      action: () => ({ message: 'This should not appear' })
    });

    // Create event
    const event = createEvent(
      StandardEventTypes.ITEM_TAKEN,
      { itemId: 'apple' },
      { actor: 'player', target: 'apple' }
    );

    // Process the event
    const result = ruleSystem.processEvent(event, world);

    // Check that only prevent rule ran
    expect(result.prevent).toBe(true);
    expect(result.events).toHaveLength(1);
    expect(result.events[0].payload.message).toBe('Action prevented!');
  });

  test('Should handle conditional rules', () => {
    // Add rule with condition
    ruleSystem.addRule({
      id: 'conditional-rule',
      eventType: 'item:taken',
      condition: (event, world) => {
        const item = getTargetItem(event, world);
        return getAttribute(item, 'name') === 'apple';
      },
      action: () => ({ message: 'You take the delicious apple!' })
    });

    // Test with apple (should match)
    let event = createEvent(
      StandardEventTypes.ITEM_TAKEN,
      { itemId: 'apple' },
      { actor: 'player', target: 'apple' }
    );

    let result = ruleSystem.processEvent(event, world);
    expect(result.events).toHaveLength(1);
    expect(result.events[0].payload.message).toBe('You take the delicious apple!');

    // Test with statue (should not match)
    event = createEvent(
      StandardEventTypes.ITEM_TAKEN,
      { itemId: 'heavy-statue' },
      { actor: 'player', target: 'heavy-statue' }
    );

    result = ruleSystem.processEvent(event, world);
    expect(result.events).toHaveLength(0);
  });
});

/**
 * Sharpee Languages - Built-in language system
 * 
 * This module provides the new author-centric language system where
 * authors can simply call story.languageSet(US_EN) and everything works.
 */

export * from './constants';
export * from './types';
export * from './registry';

// Import and register built-in language definitions
import { getLanguageRegistry } from './registry';
import { createEnglishDefinition } from './definitions/english';

// Auto-register built-in languages
const registry = getLanguageRegistry();

// Register English as the primary language
registry.register(createEnglishDefinition());

// Future: Register other languages
// registry.register(createFrenchDefinition());
// registry.register(createSpanishDefinition());

/**
 * Convenience exports for common usage
 */
import { createLanguageInstance, getLanguageInstance } from './registry';
import { US_EN } from './constants';

export {
  createLanguageInstance,
  getLanguageInstance
};

/**
 * Helper function to create an English language instance
 * This is what will be used internally by the parser test and Story class
 */
export function createEnglishLanguageInstance() {
  return createLanguageInstance(US_EN);
}

/**
 * Helper function to get parser config from language code
 * This provides compatibility with the existing parser architecture
 */
export function getParserConfigForLanguage(languageCode: string) {
  const instance = getLanguageInstance(languageCode as any);
  return instance.getParserConfig();
}

/**
 * Helper function to get grammar patterns from language code
 * This provides compatibility with the existing parser architecture
 */
export function getGrammarPatternsForLanguage(languageCode: string) {
  const instance = getLanguageInstance(languageCode as any);
  return instance.getGrammarPatterns();
}

/**
 * Create a parser provider compatible with the existing IF parser
 * This allows the test to work without importing external packages
 */
export function createEnglishParserProvider() {
  const instance = createEnglishLanguageInstance();

  return {
    getParserConfig() {
      return instance.getParserConfig();
    },
    getGrammarPatterns() {
      return instance.getGrammarPatterns();
    },
    getSynonyms(word: string) {
      return instance.getSynonyms(word);
    }
  };
}

/**
 * Scope calculator for IF visibility and reachability
 * 
 * Determines what entities are visible, reachable, and known to the player
 * based on IF rules for darkness, containers, and distance.
 */

import { EntityId } from '../types';
import { IFWorld } from './if-world';
import { 
  IFEntity,
  IFAttributes,
  isRoom,
  isContainer,
  isPerson,
  providesLight
} from '../if-entities/types';

export namespace ScopeCalculator {
  /**
   * Result of scope calculation
   */
  export interface ScopeResult {
    /** Entities that can be seen */
    visible: Set<EntityId>;
    /** Entities that can be touched/interacted with */
    reachable: Set<EntityId>;
    /** Entities the player knows about */
    known: Set<EntityId>;
    /** Whether the player is in darkness */
    inDarkness: boolean;
    /** Current light level (0 = pitch black, 1 = normal) */
    lightLevel: number;
  }
}

/**
 * Calculates scope (visibility and reachability) in the IF world
 */
export class ScopeCalculator {
  constructor(private world: IFWorld) {}

  /**
   * Calculate scope for an entity (usually the player)
   */
  calculateScope(viewerId: EntityId): ScopeCalculator.ScopeResult {
    const viewer = this.world.getEntity(viewerId);
    if (!viewer) {
      return {
        visible: new Set(),
        reachable: new Set(),
        known: new Set(),
        inDarkness: true,
        lightLevel: 0
      };
    }

    const viewerLocation = this.world.getLocation(viewerId);
    if (!viewerLocation) {
      return {
        visible: new Set([viewerId]),
        reachable: new Set([viewerId]),
        known: new Set([viewerId]),
        inDarkness: false,
        lightLevel: 1
      };
    }

    // Calculate light level
    const { inDarkness, lightLevel } = this.calculateLighting(viewerLocation, viewerId);

    // Calculate visibility
    const visible = this.calculateVisibility(viewerId, viewerLocation, inDarkness);

    // Calculate reachability (subset of visible)
    const reachable = this.calculateReachability(viewerId, viewerLocation, visible);

    // Calculate known entities (everything the player has seen)
    const known = this.calculateKnown(viewerId);

    return {
      visible,
      reachable,
      known,
      inDarkness,
      lightLevel
    };
  }

  /**
   * Calculate lighting conditions
   */
  private calculateLighting(locationId: EntityId, viewerId: EntityId): {
    inDarkness: boolean;
    lightLevel: number;
  } {
    const location = this.world.getEntity(locationId);
    if (!location || !isRoom(location)) {
      return { inDarkness: false, lightLevel: 1 };
    }

    // Check if room is dark
    const isDark = (location.attributes.dark as boolean) ?? false;
    if (!isDark) {
      return { inDarkness: false, lightLevel: 1 };
    }

    // Look for light sources
    let lightLevel = 0;

    // Check viewer
    if (providesLight(this.world.getEntity(viewerId)!)) {
      lightLevel = 1;
    }

    // Check carried items
    const carried = this.world.getContents(viewerId);
    for (const item of carried) {
      if (providesLight(item)) {
        lightLevel = Math.max(lightLevel, 1);
      }
    }

    // Check room contents
    const roomContents = this.world.getContents(locationId);
    for (const item of roomContents) {
      if (providesLight(item)) {
        lightLevel = Math.max(lightLevel, 0.8);
      }

      // Check inside open containers
      if (isContainer(item) && item.attributes.open) {
        const containerContents = this.world.getContents(item.id);
        for (const inner of containerContents) {
          if (providesLight(inner)) {
            lightLevel = Math.max(lightLevel, 0.6);
          }
        }
      }
    }

    return {
      inDarkness: lightLevel === 0,
      lightLevel
    };
  }

  /**
   * Calculate what's visible
   */
  private calculateVisibility(
    viewerId: EntityId,
    viewerLocation: EntityId,
    inDarkness: boolean
  ): Set<EntityId> {
    const visible = new Set<EntityId>();

    // Viewer can always see themselves
    visible.add(viewerId);

    // In total darkness, can only see self and carried light sources
    if (inDarkness) {
      const carried = this.world.getContents(viewerId);
      for (const item of carried) {
        if (providesLight(item)) {
          visible.add(item.id);
        }
      }
      return visible;
    }

    // Add the current location
    visible.add(viewerLocation);

    // Add everything in the room
    this.addVisibleInLocation(viewerLocation, visible);

    // Add carried items
    const carried = this.world.getContents(viewerId);
    for (const item of carried) {
      visible.add(item.id);
      // And contents of open/transparent containers
      if (isContainer(item) && (item.attributes.open || item.attributes.transparent)) {
        this.addVisibleInContainer(item.id, visible);
      }
    }

    // Add worn items
    const viewer = this.world.getEntity(viewerId)!;
    for (const item of carried) {
      if (item.attributes.worn) {
        visible.add(item.id);
      }
    }

    return visible;
  }

  /**
   * Add visible items in a location
   */
  private addVisibleInLocation(locationId: EntityId, visible: Set<EntityId>): void {
    const contents = this.world.getContents(locationId);

    for (const entity of contents) {
      const attrs = entity.attributes as IFAttributes;

      // Skip concealed items
      if (attrs.concealed) continue;

      // Add the item
      visible.add(entity.id);

      // Look inside open/transparent containers
      if (isContainer(entity) && (attrs.open || attrs.transparent)) {
        this.addVisibleInContainer(entity.id, visible);
      }

      // Look on supporters
      if (attrs.supporter) {
        this.addVisibleInLocation(entity.id, visible);
      }
    }
  }

  /**
   * Add visible items in a container
   */
  private addVisibleInContainer(containerId: EntityId, visible: Set<EntityId>): void {
    const contents = this.world.getContents(containerId);

    for (const entity of contents) {
      visible.add(entity.id);

      // Recursively check nested containers
      if (isContainer(entity) && (entity.attributes.open || entity.attributes.transparent)) {
        this.addVisibleInContainer(entity.id, visible);
      }
    }
  }

  /**
   * Calculate what's reachable
   */
  private calculateReachability(
    viewerId: EntityId,
    viewerLocation: EntityId,
    visible: Set<EntityId>
  ): Set<EntityId> {
    const reachable = new Set<EntityId>();

    // Viewer can reach themselves
    reachable.add(viewerId);

    // Can reach current location
    reachable.add(viewerLocation);

    // Can reach carried/worn items
    const carried = this.world.getContents(viewerId);
    for (const item of carried) {
      reachable.add(item.id);

      // Can reach inside open containers we're carrying
      if (isContainer(item) && item.attributes.open) {
        const contents = this.world.getContents(item.id);
        for (const inner of contents) {
          reachable.add(inner.id);
        }
      }
    }

    // Can reach items in the room (if visible)
    const roomContents = this.world.getContents(viewerLocation);
    for (const entity of roomContents) {
      if (!visible.has(entity.id)) continue;

      const attrs = entity.attributes as IFAttributes;

      // Can't reach things inside closed containers
      if (isContainer(entity) && !attrs.open) {
        reachable.add(entity.id); // Can reach the container itself
        continue;
      }

      // Can reach the item
      reachable.add(entity.id);

      // Can reach things in/on open containers and supporters
      if ((isContainer(entity) && attrs.open) || attrs.supporter) {
        const contents = this.world.getContents(entity.id);
        for (const inner of contents) {
          if (visible.has(inner.id)) {
            reachable.add(inner.id);
          }
        }
      }
    }

    return reachable;
  }

  /**
   * Calculate known entities (simplified for now)
   */
  private calculateKnown(viewerId: EntityId): Set<EntityId> {
    // For now, known = everything visible
    // In a full implementation, this would track what the player has seen before
    const { visible } = this.calculateScope(viewerId);
    return new Set(visible);
  }
}

