/**
 * Language registry for built-in language definitions
 */

import { LanguageDefinition, LanguageRegistry, LanguageInstance, LanguageOptions } from './types';
import { SupportedLanguage, DEFAULT_LANGUAGE } from './constants';

/**
 * Simple language registry implementation
 */
class SimpleLanguageRegistry implements LanguageRegistry {
  private definitions = new Map<SupportedLanguage, LanguageDefinition>();

  register(definition: LanguageDefinition): void {
    this.definitions.set(definition.code, definition);
  }

  get(code: SupportedLanguage): LanguageDefinition | undefined {
    return this.definitions.get(code);
  }

  has(code: SupportedLanguage): boolean {
    return this.definitions.has(code);
  }

  getRegisteredCodes(): SupportedLanguage[] {
    return Array.from(this.definitions.keys());
  }
}

/**
 * Global language registry instance
 */
const globalRegistry = new SimpleLanguageRegistry();

/**
 * Get the global language registry
 */
export function getLanguageRegistry(): LanguageRegistry {
  return globalRegistry;
}

/**
 * Language instance implementation
 */
export class LanguageInstanceImpl implements LanguageInstance {
  constructor(
    public readonly definition: LanguageDefinition,
    public readonly options: LanguageOptions = {}
  ) {}

  getMessage(key: string): string {
    // Check custom messages first, then fall back to definition
    return this.options.customMessages?.[key] ?? this.definition.messages[key] ?? key;
  }

  formatMessage(key: string, ...params: any[]): string {
    const template = this.getMessage(key);
    return this.interpolateTemplate(template, params);
  }

  getVerb(canonical: string): VerbDefinition | undefined {
    // Check custom verbs first, then fall back to definition
    return this.options.customVerbs?.[canonical] ?? this.definition.verbs[canonical];
  }

  isVerb(word: string): boolean {
    const canonical = this.getCanonicalVerb(word);
    return canonical !== undefined;
  }

  getCanonicalVerb(verb: string): string | undefined {
    const lowerVerb = verb.toLowerCase();

    // Check if it's already canonical
    for (const [canonical, verbDef] of Object.entries(this.definition.verbs)) {
      if (canonical === lowerVerb) {
        return canonical;
      }

      // Check synonyms
      if (verbDef.synonyms.includes(lowerVerb)) {
        return canonical;
      }
    }

    // Check custom verbs
    if (this.options.customVerbs) {
      for (const [canonical, verbDef] of Object.entries(this.options.customVerbs)) {
        if (canonical === lowerVerb) {
          return canonical;
        }

        if (verbDef.synonyms.includes(lowerVerb)) {
          return canonical;
        }
      }
    }

    return undefined;
  }

  getSynonyms(word: string): string[] {
    const lowerWord = word.toLowerCase();

    // Check definition synonyms
    const definitionSynonyms = this.definition.synonyms.get(lowerWord) || [];

    // Check custom synonyms
    const customSynonyms = this.options.customSynonyms?.[lowerWord] || [];

    // Combine and deduplicate
    const combined = [...definitionSynonyms, ...customSynonyms];
    return Array.from(new Set(combined));
  }

  getParserConfig(): IFParserConfig {
    return this.definition.parser;
  }

  getGrammarPatterns(): GrammarPattern[] {
    return this.definition.patterns;
  }

  private interpolateTemplate(template: string, params: any[]): string {
    return template.replace(/\{(\d+)\}/g, (match, index) => {
      const paramIndex = parseInt(index, 10);
      return paramIndex < params.length ? String(params[paramIndex]) : match;
    });
  }
}

/**
 * Create a language instance from a language code and options
 */
export function createLanguageInstance(
  code: SupportedLanguage, 
  options: LanguageOptions = {}
): LanguageInstance {
  const definition = globalRegistry.get(code);
  if (!definition) {
    throw new Error(`Language '${code}' is not registered. Available languages: ${globalRegistry.getRegisteredCodes().join(', ')}`);
  }

  return new LanguageInstanceImpl(definition, options);
}

/**
 * Helper function to ensure a language is available, falling back to default
 */
export function getLanguageInstance(
  code: SupportedLanguage, 
  options: LanguageOptions = {}
): LanguageInstance {
  // Try the requested language first
  if (globalRegistry.has(code)) {
    return createLanguageInstance(code, options);
  }

  // Fall back to default language
  if (code !== DEFAULT_LANGUAGE && globalRegistry.has(DEFAULT_LANGUAGE)) {
    console.warn(`Language '${code}' not available, falling back to '${DEFAULT_LANGUAGE}'`);
    return createLanguageInstance(DEFAULT_LANGUAGE, options);
  }

  throw new Error(`Neither '${code}' nor default language '${DEFAULT_LANGUAGE}' are available`);
}

